INSERT INTO `dos_module_entertainment` (`record_id`, `pic_thumb`, `title`, `titleen`, `titlefr`, `preview`, `previewen`, `previewfr`, `content`, `contenten`, `contentfr`, `tag`, `description`, `author`, `hits`, `postdate`, `record_order`, `record_type`, `extra_field1`, `extra_field2`, `enable`, `dos_module_item_cat_cat_id`) VALUES
(1, 'clock.gif', 'Tin nóng buổi chiều', '', '', '<p style="text-align:justify;">\n	Một giám đốc doanh nghiệp vừa bị phá sản với dáng vẻ buồn bã và thất vọng, đứng trên thành cầu đăm đăm nhìn xuống dòng nước xoáy cuộn.</p>\n', '', '', '<p style="text-align:justify;">\n	Một giám đốc doanh nghiệp vừa bị phá sản với dáng vẻ buồn bã và thất vọng, đứng trên thành cầu đăm đăm nhìn xuống dòng nước xoáy cuộn. Sau lưng anh ta, một phóng viên bồn chồn đi lại với chiếc máy ảnh trong tay. Cuối cùng, tay nhà báo sốt ruột xem đồng hồ rồi chạy lên nói với vị giám đốc chán đời:</p>\n<p style="text-align:justify;">\n	- Này anh bạn, quyết định rồi thì nhảy sớm đi, chỉ còn có 3 phút nữa thôi đấy!</p>\n<p style="text-align:justify;">\n	- Đối với tôi thời gian đâu có ý nghĩa gì?</p>\n<p style="text-align:justify;">\n	- Nhưng chậm hơn thì anh sẽ không kịp lọt vào mục tin nóng buổi chiều đâu!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'tin-nong-buoi-chieu', 'Tin buổi chiều', NULL, 136, '2012-01-06 09:31:52', 49, 0, NULL, NULL, 1, 3),
(3, 'y2.jpg', 'Lọt vào mắt', '', '', '<p>\n	Hai anh bạn đồng nghiệp trò chuyện:</p>\n<p>\n	- Tuần trước, một hạt cát lọt vào mắt vợ tôi, phải đi bác sĩ gắp ra mất 50 nghìn đồng.</p>\n', '', '', '<p>\n	Hai anh bạn đồng nghiệp trò chuyện:</p>\n<p>\n	- Tuần trước, một hạt cát lọt vào mắt vợ tôi, phải đi bác sĩ gắp ra mất 50 nghìn đồng.</p>\n<p>\n	- Nhằm nhò gì, tuần trước nữa, cái váy lọt vào mắt vợ tôi, tôi phải tốn 500 nghìn đồng đấy.</p>\n<p>\n	Sếp của hai gã lên tiếng:</p>\n<p>\n	- Hãy dỏng tai lên nghe và đừng có mà than thở, một tên cao 1m85, nặng 80 kg lọt vào mắt của vợ tôi, thế là tôi đi tong một nửa gia sản.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'lot-vao-mat', 'Lọt vào mắt', NULL, 117, '2012-01-06 10:26:13', 52, 0, NULL, NULL, 1, 3),
(4, '12746684130803dep.jpg', 'Không thấy BMW', '', '', '<p>\n	Một giám đốc đến công ty làm việc nhưng quên kéo phéc mơ tuya quần, cô thư ký nhìn thấy bèn nhắc khéo: Sếp ơi hôm nay sếp quên đóng của ga ra lại ạ.</p>\n', '', '', '<p style="text-align:justify;">\n	Một giám đốc đến công ty làm việc nhưng quên kéo phéc mơ tuya quần, cô thư ký nhìn thấy bèn nhắc khéo: Sếp ơi hôm nay sếp quên đóng của ga ra ạ.</p>\n<p style="text-align:justify;">\n	Ông ta giật mình nhìn xuống, thẹn quá bèn chữa thẹn: Thế cô đi ngang qua đấy có thấy chiếc BMW của tôi đậu trong đấy không?</p>\n<p style="text-align:justify;">\n	Dạ không sếp ạ, em chỉ thấy một chiếc xe đạp xẹp lốp nằm ngoẹo cổ sang một bên thôi. Cô thư ký trả lời.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'khong-thay-bmw', 'BMW', NULL, 128, '2012-01-06 10:36:12', 49, 0, NULL, NULL, 1, 3),
(5, 'kenhsinhviennetsakilaquiz.jpg', 'Thực hành tại chổ', '', '', '<p style="text-align:justify;">\n	Một nữ sinh viên vừa được tuyển vào bán hàng ở quầy máy tính tại một trung tâm thương mại. Người quản lý liền dẫn cô đi tham quan các quầy để truyền thụ kinh nghiệm.</p>\n', '', '', '<p style="text-align:justify;">\n	Một nữ sinh viên vừa được tuyển vào bán hàng ở quầy máy tính tại một trung tâm thương mại. Người quản lý liền dẫn cô đi tham quan các quầy để truyền thụ kinh nghiệm.</p>\n<p style="text-align:justify;">\n	Khi đến khu bán dụng cụ làm vườn, họ nghe thấy một khách hàng hỏi mua hạt giống cỏ nhung. Viên quản lý lập tức bước lại gần và hỏi:</p>\n<p style="text-align:justify;">\n	"Xin lỗi ông, tôi nghĩ có lẽ ông cũng cần ống phun nước để tưới cây?"</p>\n<p style="text-align:justify;">\n	"Tôi cũng nghĩ vậy. Ông cho tôi một cuộn".</p>\n<p style="text-align:justify;">\n	"Ông lấy thêm phân bón và thuốc diệt cỏ dại không?"</p>\n<p style="text-align:justify;">\n	"Ừm, cũng được".</p>\n<p style="text-align:justify;">\n	"Ông cũng nên sắm một máy cắt cỏ để giữ cho bãi cỏ luôn bằng phẳng".</p>\n<p style="text-align:justify;">\n	"Ý kiến của ông hay đó. Cho tôi một chiếc luôn".</p>\n<p style="text-align:justify;">\n	Khi ông khách đi khỏi, viên quản lý nói với cô nhân viên mới:</p>\n<p style="text-align:justify;">\n	"Cô thấy không, đó là một cách hữu hiệu để bán được nhiều hàng".</p>\n<p style="text-align:justify;">\n	Trở về quầy máy tính của mình, cô nhân viên hồ hởi nói với vị khách đầu tiên của mình:</p>\n<p style="text-align:justify;">\n	"Tôi nghĩ có lẽ anh cũng cần một ống phun nước".</p>\n<p style="text-align:justify;">\n	"Tại sao tôi phải cần đến nó?"</p>\n<p style="text-align:justify;">\n	"Vì... khi anh làm việc nhiều, máy tính sẽ nóng và dễ bị hư. Anh cần một cái để xịt nước cho nó nguội lại".</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'thuc-hanh-tai-cho', 'Nhân viên', NULL, 114, '2012-01-06 10:47:08', 53, 0, NULL, NULL, 1, 3),
(6, 'ttm-nha-tao.JPG', 'Nhà doanh nghiệp khôn ngoan', '', '', '<p>\n	Con của ta. Nhà doanh nghiệp sắp chết nói với con.</p>\n', '', '', '<p>\n	Con của ta. Nhà doanh nghiệp sắp chết nói với con.</p>\n<p style="text-align:justify;">\n	Cha thành đạt được là nhờ hai phương châm: Trung tín và khôn ngoan. Trung tín là nếu con đã hứa giao thứ gì thì bất kể trường hợp nào, dù phải phá sản đi nữa, con cũng cứ giao.</p>\n<p style="text-align:justify;">\n	Con xin ghi nhớ điều đó, thưa cha. Người con đáp.</p>\n<p style="text-align:justify;">\n	Còn khôn ngoan là thế nào ạ?</p>\n<p style="text-align:justify;">\n	Khôn ngoan thì đơn giản thôi, con đừng hứa bất cứ điều gì với ai hết. Người cha trả lời.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nha-doanh-nghiep-khon-ngoan', 'Khôn ngoan', NULL, 102, '2012-01-07 11:30:23', 50, 0, NULL, NULL, 1, 3),
(7, 'tranhvuitomotranhheo.jpg', 'Giám đốc công ty chăn nuôi', '', '', '<p style="text-align:justify;">\n	Một hôm, giám đốc công ty chăn nuôi lệnh cho "ra lò" cuốn "Nội san" công ty và đăng ảnh của ông ở trong đó.</p>\n', '', '', '<p style="text-align:justify;">\n	Một hôm, giám đốc công ty chăn nuôi lệnh cho "ra lò" cuốn "Nội san" công ty và đăng ảnh của ông ở trong đó.</p>\n<p style="text-align:justify;">\n	Để cho tấm ảnh thật sự "sinh động", ông bèn xắn quần áo đứng vào giữa đám heo trong chuồng.</p>\n<p style="text-align:justify;">\n	Và vấn đề khó với ban biên tập là chú thích cái ảnh giám đốc đứng bên cạnh 6 con heo to béo sao cho phù hợp???</p>\n<p style="text-align:justify;">\n	- Ta nên chú thích: Giám đốc trong chuồng heo</p>\n<p style="text-align:justify;">\n	- Nghe không ổn, hơi bình dân</p>\n<p style="text-align:justify;">\n	- Giữa đàn heo là giám đốc công ty..., có được không?</p>\n<p style="text-align:justify;">\n	- Cũng không được! Cứ gờn gợn cái gì đấy.</p>\n<p style="text-align:justify;">\n	Sau khi họp lên họp xuống, trưởng ban biên tập quyết định:</p>\n<p style="text-align:justify;">\n	- Đặt tên thế nào thì đặt, không được nhắc đến chữ heo đi kèm chữ giám đốc.</p>\n<p style="text-align:justify;">\n	Vài ngày sau, trên trang bìa cuốn "Nội san" đăng trang trọng ảnh ông giám đốc với chú thích to tướng:</p>\n<p style="text-align:justify;">\n	<strong>"Giám đốc công ty..., đứng thứ 7 từ trái sang"</strong></p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'giam-doc-cong-ty-chan-nuoi', 'Giám đốc công ty chăn nuôi ', NULL, 147, '2012-01-08 05:44:49', 54, 0, NULL, NULL, 1, 3),
(8, '1324205025_tranhvui200165.jpg', 'Rất nhanh', '', '', '<p style="text-align:justify;">\n	Một ông vừa được lên chức giám đốc. Ngày đầu đi làm về, vợ hỏi:</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Tự khai</strong></p>\n<p style="text-align:justify;">\n	Một ông vừa được lên chức giám đốc. Ngày đầu đi làm về, vợ hỏi:</p>\n<p style="text-align:justify;">\n	- Sao, văn phòng của anh có đẹp không?</p>\n<p style="text-align:justify;">\n	- Đẹp.</p>\n<p style="text-align:justify;">\n	- Có nữ thư ký trẻ không?</p>\n<p style="text-align:justify;">\n	- Có.</p>\n<p style="text-align:justify;">\n	- Cô ta ăn mặc như thế nào?</p>\n<p style="text-align:justify;">\n	- Rất nhanh.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'rat-nhanh', 'nu thu ky, dep, an mac, van phong, giam doc', NULL, 109, '2012-01-09 11:51:51', 50, 0, NULL, NULL, 1, 3),
(9, 'baby11.jpg', 'Ế ẩm do khách hàng phát triển', '', '', '<p style="text-align:justify;">\n	Hai anh bạn gặp nhau:</p>\n<p style="text-align:justify;">\n	- Chào cậu, thế nào? Cửa hàng của cậu kinh doanh có phát đạt không?</p>\n', '', '', '<p style="text-align:justify;">\n	Hai anh bạn gặp nhau:</p>\n<p style="text-align:justify;">\n	- Chào cậu, thế nào? Cửa hàng của cậu kinh doanh có phát đạt không?</p>\n<p style="text-align:justify;">\n	- Đâu có! Khách hàng của tớ cứ ngày càng phát triển, nên làm ăn khó khăn lắm!</p>\n<p style="text-align:justify;">\n	- Cậu nói gì lạ vậy? Tưởng khách hàng phát triển thì kinh doanh cũng phát đạt chứ!</p>\n<p style="text-align:justify;">\n	- Có gì đâu mà không hiểu! Tớ kinh doanh quần áo trẻ em mà!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'e-am-do-khach-hang-phat-trien', 'Hai anh bạn gặp nhau:\n\n- Chào cậu, thế nào? Cửa hàng của cậu kinh doanh có phát đạt không?\n\n- Đâu có! Khách hàng của tớ cứ ngày càng phát triển, nên làm ăn khó khăn lắm!\n\n- Cậu nói gì lạ vậy? Tưởng khách hàng phát triển thì kinh doanh cũng phát đạt c', NULL, 117, '2012-01-12 03:50:08', 50, 0, NULL, NULL, 1, 3),
(10, 'thu-gian-dad.jpg', 'Sếp là phải sáng tạo', '', '', '<p style="text-align:justify;">\n	Giám đốc đi làm về đang nằm ghế bố đọc báo chờ ăn cơm chiều thì tiếng chuông điện thoại di động reo. Bà vợ giám đốc lắng tai nghe</p>\n', '', '', '<p style="text-align:justify;">\n	Giám đốc đi làm về đang nằm ghế bố đọc báo chờ ăn cơm chiều thì tiếng chuông điện thoại di động reo. Bà vợ giám đốc lắng tai nghe.</p>\n<p style="text-align:justify;">\n	Giám đốc: A lô</p>\n<p style="text-align:justify;">\n	Bồ nhí: Anh còn nhớ em không?</p>\n<p style="text-align:justify;">\n	Giám đốc: Nguyễn Văn Còn</p>\n<p style="text-align:justify;">\n	Bồ nhí: Hôm nay mình gặp nhau nha?</p>\n<p style="text-align:justify;">\n	Giám đốc: Lê Văn Bận</p>\n<p style="text-align:justify;">\n	Bồ nhí: Vậy khi nào gặp?</p>\n<p style="text-align:justify;">\n	Giám đốc: Trần Văn Mai</p>\n<p style="text-align:justify;">\n	Bồ nhí: Vẫn ở chỗ cũ hả?</p>\n<p style="text-align:justify;">\n	Giám đốc: Nguyễn Y Vân (Vẫn y nguyên)</p>\n<p style="text-align:justify;">\n	Bồ nhí: Anh hứa nha!!!</p>\n<p style="text-align:justify;">\n	Giám đốc: Ngô Văn Hứa.</p>\n<p style="text-align:justify;">\n	Hứa xong, giám đốc cúp máy cái rụp, nói rõ to cho "sư tử " nghe: Bực mình,  có mỗi cái danh sách khen thưởng mà ban thi đua cũng không nhớ.</p>\n<p style="text-align:justify;">\n	Nói xong, ông ta tự nhủ: Làm sếp là phải biết sáng tạo, không thì... chết chắc.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'sep-la-phai-sang-tao', 'Hứa xong, giám đốc cúp máy cái rụp, nói rõ to cho "sư tử " nghe: Bực mình, có mỗi cái danh sách thưởng mà ban thi đua cũng không nhớ ', NULL, 134, '2012-01-12 04:02:28', 50, 0, NULL, NULL, 1, 3),
(11, 'bac_si021.gif', 'Chờ giải phẩu tử thi', '', '', '<p style="text-align:justify;">\n	Thấy sức khỏe ngày càng sa sút, vị doanh nhân nọ đi khám bác sĩ. Bác sĩ đưa ra rất nhiều chẩn đoán xấu về tình trạng sức khỏe, làm cho ông ta hoang mang lo lắng nên quyết định đi khám ở một số nơi nữa.</p>\n', '', '', '<p style="text-align:justify;">\n	Thấy sức khỏe ngày càng sa sút, vị doanh nhân nọ đi khám bác sĩ. Bác sĩ đưa ra rất nhiều chẩn đoán xấu về tình trạng sức khỏe, làm cho ông ta hoang mang lo lắng nên quyết định đi khám ở một số nơi nữa.</p>\n<p style="text-align:justify;">\n	Khi gặp lại vị bác sĩ hôm nọ, vị doanh nhân trách:</p>\n<p style="text-align:justify;">\n	- Tôi đã đi khám ở 3 nơi nữa, chẳng nơi nào đồng ý với chẩn đoán của ông.</p>\n<p style="text-align:justify;">\n	- Được rồi! Hãy chờ đến khi giải phẫu tử thi rồi sẽ biết ai đúng ai sai! Bác sĩ bình thản đáp.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'cho-giai-phau-tu-thi', 'Cảm thấy sức khoẻ không được tốt, vị doanh nhân nọ đi khám bác sĩ. Vị bác sĩ đưa ra rất nhiều chẩn đoán xấu về tình trạng của doanh nhân. Doanh nhân hoang mang lo lắng nên quyết định đi khám ở một số nơi nữa.\n', NULL, 145, '2012-01-15 04:23:39', 55, 0, NULL, NULL, 1, 3),
(12, 'doc-dao.jpg', 'Những kiểu ngủ chỉ có ở dân văn phòng', '', '', '<p style="text-align:justify;">\n	Quả là sức sáng tạo của con người không có giới hạn.</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Quả là sức sáng tạo của con người không có giới hạn.</strong></p>\n<p style="text-align:justify;">\n	Ghé thăm văn phòng của các công ty vào giờ nghỉ trưa, chắc chắn các bạn sẽ bắt gặp các nhân viên văn phòng ngủ trưa trong tư thế “khôn đụng hàng”. Chỉ với bàn và ghế mà có tới “nghìn lẻ một” kiểu ngủ trưa lạ lùng đã ra đời.</p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_1.jpg" style="width:500px;height:290px;" /><br /><span style="color:#b22222;"><em>Xếp 2 chiếc ghế lại, rồi đánh một giấc thật say, có lẽ đây là kiểu ngủ phổ biến nhất.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_2.jpg" style="width:500px;height:291px;" /><br /><span style="color:#b22222;"><em>Hay đơn giản, cứ thế gục lên bàn mà ngủ thôi. Tuy nhiên, tư thế này mỏi lưng lắm nhé.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_3.jpg" style="width:500px;height:333px;" /><br /><span style="color:#b22222;"><em>Ngủ say quá, đầu lìa khỏi bàn lúc nào còn không biết </em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_4.jpg" style="width:500px;height:331px;" /><br /><span style="color:#b22222;"><em>Ngủ cũng phải đúng “mốt” nằm xấp – planking đang rất thịnh hành chứ.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_5.jpg" style="width:500px;height:333px;" /><br /><span style="color:#b22222;"><em>Ôi trời ơi, đi làm có cái giường êm thế kia thì làm thế nào thức cho được?</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_6.jpg" style="width:500px;height:331px;" /><br /><span style="color:#b22222;"><em>Viết thêm cái bảng còn lâu mới tới giờ làm việc để ngủ cho chắc.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_7.jpg" style="width:500px;height:331px;" /><br /><span style="color:#b22222;"><em>Mùa đông mà được ngủ nướng trên chiếc đệm sưởi thì còn gì bằng.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_8.jpg" style="width:500px;height:331px;" /><br /><span style="color:#b22222;"><em>Ố ồ, có ai đã nằm ngủ kiểu này chưa? </em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_11.jpg" style="width:500px;height:375px;" /><br /><span style="color:#b22222;"><em>Tận dụng mọi địa thế, địa hình để mà ngủ cho ngon.</em></span></p>\n<p style="text-align:center;">\n	<span style="color:rgb(178,34,34);"><em><img alt="" src="/public/userfiles/images/ngu_van_phong_12.jpg" style="width:500px;height:331px;" /></em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_13.jpg" style="width:500px;height:332px;" /></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_14.jpg" style="width:500px;height:291px;" /><br /><span style="color:#b22222;"><em>Ngủ cũng phải sáng tạo chứ.</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ngu_van_phong_15.jpg" style="width:500px;height:331px;" /><br /><span style="color:#b22222;"><em>Nhìn chị ấy ngon giấc thế, chẳng ai lỡ đánh thức đâu.</em></span></p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nhung-kieu-ngu-chi-co-o-dan-van-phong', 'ngủ kiểu văn phòng', NULL, 144, '2012-01-22 13:34:32', 53, 0, NULL, NULL, 1, 10),
(13, '1326944816truyentranhhai1.jpg', 'Thưởng tết bằng séc', '', '', '<p style="text-align:justify;">\n	Năm nay các bạn làm việc rất tốt. Lợi nhuận của công ty đã tăng một cách đáng kể. Để thưởng cho các bạn, tôi sẽ phát cho mỗi người một tấm séc trị giá 5 triệu đồng…<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Cuối năm tổng kết công ty, sếp vui vẻ thông báo:</p>\n<p style="text-align:justify;">\n	- Năm nay các bạn làm việc rất tốt. Lợi nhuận của công ty đã tăng một cách đáng kể. Để thưởng cho các bạn, tôi sẽ phát cho mỗi người một tấm séc trị giá 5 triệu đồng…</p>\n<p style="text-align:justify;">\n	Tất cả nhân viên công ty vỗ tay rào rào tán thưởng.</p>\n<p style="text-align:justify;">\n	Sếp tiếp lời: Nếu năm sau các bạn vẫn làm việc nhiệt tình như thế này, hãy đem tờ séc đó đến gặp tôi và tôi sẽ… k‎ý chúng.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'thuong-tet-bang-sec', 'Thưởng Tết của sếp\nCuối năm tổng kết công ty, sếp vui vẻ thông báo:\n- Năm nay các bạn làm việc rất tốt. Lợi nhuận của công ty đã tăng một cách đáng kể. Để thưởng cho các bạn, tôi sẽ phát cho mỗi người một tấm séc trị giá 5 triệu đồng…', NULL, 110, '2012-01-22 14:31:27', 57, 0, NULL, NULL, 1, 3),
(14, 'ormie1.jpg', 'Nude đến từng centimet', '', '', '<p style="text-align:justify;">\n	”... tạo cho người xem một cảm giác thèm muốn khó tả, sự sắp đặt tuyệt vời …..”.</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Chỉ gói gọn trong 3 tấm ảnh, nhưng nhiều người xem đều đưa ra kết luận chung ” nghệ thuật nhiếp ảnh được nâng lên một tầm cao mới, một bố cục gọn không thể nào gọn hơn, không dung tục nhưng tạo cho người xem một cảm giác thèm muốn khó tả, sự sắp đặt tuyệt vời …..”</strong></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ga-quay.jpg" style="width:500px;height:666px;" /></p>\n<p style="text-align:center;">\n	 </p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/ga-quay2.jpg" style="width:500px;height:375px;" /></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/heo-quay.jpg" style="width:500px;height:375px;" /></p>\n<p style="text-align:right;">\n	<span style="font-weight:bold;">PH (Sưu tầm)</span></p>\n', '', '', 'nude-den-tung-centimet', 'không mảnh vải che thân', NULL, 688, '2012-01-22 15:35:31', 58, 0, NULL, NULL, 1, 10),
(15, 'condom.jpg', 'Chiêu độc của nhà sản xuất', '', '', '<p style="text-align:justify;">\n	Một doanh nhân đến tham khảo phương pháp làm ăn ở một đất nước phát triển. Ông được mời đến một nhà máy sản xuất các sản phẩm từ cao su, vốn là một đối tác có quan hệ làm ăn từ trước.</p>\n', '', '', '<p style="text-align:justify;">\n	Một doanh nhân đến tham khảo phương pháp làm ăn ở một đất nước phát triển. Ông được mời đến một nhà máy sản xuất các sản phẩm từ cao su, vốn là một đối tác có quan hệ làm ăn từ trước.</p>\n<p style="text-align:justify;">\n	Đầu tiên, họ đưa ông tới thăm phân xưởng sản xuất núm vú chai cho trẻ em. Dây chuyền sản xuất tạo nên những tiếng "hiss, pop..." ồn ào nghe rất buồn cười.</p>\n<p style="text-align:justify;">\n	- Tiếng "hiss" là tiếng cao su được đổ vào trong khuôn - Người hướng dẫn giải thích - Còn tiếng "pop" sau đó là tiếng cây kim châm một cái lỗ vào núm vú cao su...</p>\n<p style="text-align:justify;">\n	Sau đó, họ đi tới một phân xưởng sản xuất bao cao su. Thật kỳ lạ, dây chuyển sản xuất lần này tạo nên những tiếng ồn ào kiểu "hiss, hiss, hiss, hiss, pop".</p>\n<p style="text-align:justify;">\n	- Đợi chút đã! - Doanh nhân vội nói - Tôi biết tiếng "hiss, hiss" nghĩa là gì, nhưng tại sao lại có tiếng "pop"?</p>\n<p style="text-align:justify;">\n	- À, thì cũng giống như quy trình sản xuất núm cao su vậy mà! - Người hướng dẫn trả lời - Sau cứ bốn cái bao cao su thì chúng tôi lại... chọc một cái có lỗ.</p>\n<p style="text-align:justify;">\n	- Nhưng như thế thì không tốt gì cho bao cao su!</p>\n<p style="text-align:justify;">\n	- Vâng, nhưng lại rất tốt cho việc kinh doanh núm vú cao su.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'chieu-doc-cua-nha-san-xuat', 'Một doanh nhân đến tham khảo phương pháp làm ăn ở một đất nước phát triển. Ông được mời đến một nhà máy sản xuất các sản phẩm từ cao su, vốn là một đối tác có quan hệ làm ăn từ trước.\n', NULL, 113, '2012-01-22 16:01:02', 59, 0, NULL, NULL, 1, 3),
(16, 'chon_ca_gia_tai_1jpg.jpg', 'Còn ở đâu xấu hơn', '', '', '<p style="text-align:justify;">\n	Một tối, do nhậu với nhân viên quá chén nên sếp bị xỉn.</p>\n', '', '', '<p style="text-align:justify;">\n	Một tối, do nhậu với nhân viên quá chén nên sếp bị xỉn.</p>\n<p style="text-align:justify;">\n	Các chiến thuộc cấp phải dìu sếp ra xe để đưa về nhà. Về đến nhà bà vợ sếp mở cửa ra đón.</p>\n<p style="text-align:justify;">\n	Vừa nhìn thấy vợ, sếp đứng sững lại trợn mắt nhìn rồi quát các thuộc cấp: Sao vậy, không còn nhà hàng nào có tiếp viên già và xấu hơn ở đây nữa à?</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'con-o-dau-xau-hon', 'Một tối, do uống và hát hơi nhiều nên sếp bị say xỉn. Các chiến hữu phải dìu sếp ra xe để đưa về nhà. Về đến nhà bà vợ sếp mở cửa ra đón. Vừa nhìn thấy vợ, sếp đứng sững lại trợn mắt nhìn rồi quát các chiến hữu:\n\n- Tại sao lại đến một nhà hàng có loạ', NULL, 92, '2012-01-24 09:00:39', 56, 0, NULL, NULL, 1, 3),
(17, '0675821001326683865.jpg', 'Nhà lầu và xe hơi', '', '', '<p>\n	Hai cô bạn cùng phòng ngồi tâm sự. Một cô hỏi: Thế nào, anh chàng mới quen đẹp trai chứ?</p>\n', '', '', '<p>\n	Hai cô bạn cùng phòng ngồi tâm sự. Một cô hỏi: Thế nào, anh chàng mới quen đẹp trai chứ?</p>\n<p>\n	- Đương nhiên, rất đẹp.</p>\n<p>\n	- Giàu có chứ?</p>\n<p>\n	- Đương nhiên rồi. Anh ấy có rất nhiều nhà lầu và xe hơi.</p>\n<p>\n	- Tớ ganh tị với cậu đấy. Thế anh ta làm việc cho công ty nào?</p>\n<p>\n	- Không, anh ấy là một ông chủ.</p>\n<p>\n	- Trời ơi! Tuyệt vời! Ông chủ công ty nào thế?</p>\n<p>\n	- Ông chủ cửa hàng bán lẻ đồ chơi trẻ em.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nha-lau-va-xe-hoi', 'giam doc gian hang ban doc choi', NULL, 114, '2012-01-24 09:05:27', 60, 0, NULL, NULL, 1, 3),
(18, '1.jpg', 'Bỏng mắt với những bộ ngực cực hot', '', '', '<p style="text-align:justify;">\n	Đây là những bộ ngực nóng bỏng, khiêu gợi và hấp dẫn của những ngôi sao nổi tiếng trong làng giải trí thế giới. Hãy chiêm ngưỡng "hàng độc" ngay, đừng bỏ lở cơ hội dù biết rằng tạo hóa chỉ ưu ái ban tặng riêng cho chủ nhân của nó.</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Đây là những bộ ngực nóng bỏng, khiêu gợi và hấp dẫn của những ngôi sao nổi tiếng trong làng giải trí thế giới. Hãy chiêm ngưỡng "hàng độc" ngay, đừng bỏ lở cơ hội dù biết rằng tạo hóa chỉ ưu ái ban tặng riêng cho chủ nhân của nó.</strong></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/12900.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Zac Efron xuất hiện trên bãi biển Maui ở Hawaii trong kì nghỉ vào mùa hè</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-2.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Người đàn ông đương đại sexy nhất hành tinh hugh Jackman với hình thể vạm vỡ. Anh cũng có vô số cảnh khoe ngực trong X-Men Origins: Wolverine </em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-3.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Ngôi sao True Blood – Joe Manganiello không chỉ tự hào về bộ râu quai nón quyến rũ mà còn bởi thân hình 6 múi vạm vỡ</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-4.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Mĩ nam của Chạng vạng Kellan Lutz xứng đáng là gương mặt quảng cáo ăn khách cho thương hiệu đồ Calvin Klein. Trong ảnh anh xuất hiện tren bãi biển St. Maarten ở Caribbean </em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-5.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Bradley Cooper khoe ngực trần vạm vỡ trong bộ phim The A Team</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-6.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Cựu ngôi sao của True Blood – Mechad Brooks đồng thời là người mẫu mới của thương hiệu đồ lót Calvin Klein sẵn sàng cởi áo khoe thân hình bỏng cháy trong kì nghỉ của anh ở Hawaii</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046726-sao-nam-nguc-sexy-7.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>6 năm sau khi dành danh hiệu Người đàn ông đương đại sexy nhất hành tinh Jude Law vẫn giữ được phong độ bề ngoài cực kì manly và nóng bỏng</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046800-sao-nam-nguc-sexy-8.jpg" style="width:500px;height:375px;" /><br /><span style="color:#b22222;"><em>Eddie Cibrian chưa khi nào hết thôi miên phụ nữ bởi nụ cười hút hồn của mình. Còn khi anh cởi áo, khoe ngực vẻ đẹp đó khiến nhiều người ngây ngất</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046800-sao-nam-nguc-sexy-10.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Ngôi sao của Grey Anaromy – Eric Dane đã khiến hàng triệu trái tim hâm mộ phải thổn thức với hình ảnh nóng bỏng này</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046800-sao-nam-nguc-sexy-14.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Pauly D – ngôi sao của Jersey Shore có thể đánh bại bất cứ trái tim cô gái nào khi anh xuất hiện trên bãi biển Malibu</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046861-sao-nam-nguc-sexy-21.jpg" style="width:500px;height:500px;" /><br /><span style="color:#b22222;"><em>Thương hiệu người sói đã định vị trong tâm trí người hâm mộ và để có được vẻ săn chắc cùng cơ bắp vạm vỡ Taylor đã trải qua chế độ luyện tập nghiêm ngặt</em></span></p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/1290046909-sao-nam-nguc-sexy-23.jpg" style="width:500px;height:666px;" /><br /><span style="color:#b22222;"><em>Vị chủ trò của Extra – Mario Lopez anh luôn phải vất vả mỗi ngày luyện tập để có thể giữ được thân hình hoàn hảo này</em></span></p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'bong-mat-voi-nhung-bo-nguc-cuc-hot', 'nguc dep, nong, sex, hap dan, khieu goi, goi cam', NULL, 343, '2012-01-24 10:55:29', 49, 0, NULL, NULL, 1, 10),
(19, '1296034071_tranhvui200568.jpg', 'Yêu sách của nữ thư ký', '', '', '<p>\n	Từ ngày mai, tôi muốn nhận mức lương 1.200 đô, nghỉ 4 ngày trong tuần!</p>\n', '', '', '<p>\n	Nữ thư ký nói với sếp:</p>\n<p>\n	- Từ ngày mai, tôi muốn nhận mức lương 1.200 đô, nghỉ 4 ngày trong tuần!</p>\n<p>\n	- Vậy ai nói với cô là những điều này sẽ thành hiện thực? Sếp mỉa mai hỏi.</p>\n<p>\n	- Bác sĩ phụ khoa và luật sư!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'yeu-sach-cua-nu-thu-ky', 'Nữ thư ký nói với sếp:\n\n- Từ ngày mai, tôi muốn nhận mức lương 1.200 đô, nghỉ 4 ngày trong tuần!\n\n- Vậy ai nói với cô là những điều này sẽ thành hiện thực? – Sếp mỉa mai hỏi.\n\n- Bác sĩ phụ khoa và luật sư!', NULL, 116, '2012-01-26 04:22:39', 61, 0, NULL, NULL, 1, 3),
(20, 'doi_pho_voi_giao_vien_2.jpg', 'Nghiên cứu tại khách sạn', '', '', '<p>\n	Nữ thư ký chạy vào phòng giám đốc: "Thưa sếp, có một vị khách từ sáng đến giờ đòi gặp sếp bốn lần rồi ạ!"</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Nữ thư ký chạy vào phòng giám đốc: "Thưa sếp, có một vị khách từ sáng đến giờ đòi gặp sếp bốn lần rồi ạ!"</strong></p>\n<p style="text-align:justify;">\n	- Thế tôi thuê cô để làm gì? Cô không biết tôi đang bận à?</p>\n<p style="text-align:justify;">\n	- Dạ, sếp bận gì, để em biết và trả lời với khách ạ?</p>\n<p style="text-align:justify;">\n	- Tại sao tôi phải trả lời cho cô? Cô phải biết là tôi đang chuẩn bị nội dung cho cuộc họp chiều nay, rồi còn phải nghiên cứu chỉ số chứng khoán và thẩm định các dự án đầu tư mới của công ty ta nữa chứ?</p>\n<p style="text-align:justify;">\n	10 phút sau, thư ký lại gõ cửa phòng giám đốc và thở hổn hển, mặt tái xanh:</p>\n<p style="text-align:justify;">\n	- Thưa sếp, khách để lại 3 tin nhắn</p>\n<p style="text-align:justify;">\n	Thứ nhất: Đừng để nội dung cuộc họp chiều nay trên đùi thư ký</p>\n<p style="text-align:justify;">\n	Thứ hai: Chỉ số chứng khoán không thể nằm dưới váy của thư ký được</p>\n<p style="text-align:justify;">\n	Cuối cùng: Dự án đầu tư mới của công ty, khách bảo rằng khách sạn là nơi nghiên cứu tốt nhất.</p>\n<p style="text-align:justify;">\n	- Ai... ai mà láo thế? Khách tên gì, cơ quan nào?</p>\n<p style="text-align:justify;">\n	- Dạ cơ quan và tên thật thì em không biết, nhưng khách để nickname lại là "banha...@yahoo.com" ạ!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nghien-cuu-tai-khach-san', 'sep, thu ky, ba nha', NULL, 109, '2012-01-28 11:36:50', 62, 0, NULL, NULL, 1, 3),
(21, 'dang_nao_cung_chet_2.jpg', 'Bí quyết kinh doanh', '', '', '<p>\n	Một người không quen biết đã kể lại cho tôi nghe câu chuyện làm ăn khi chúng tôi bước ra khỏi ga xe điện cuối cùng.</p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Một người không quen biết đã kể lại cho tôi nghe câu chuyện làm ăn khi chúng tôi bước ra khỏi ga xe điện cuối cùng.</strong></p>\n<p style="text-align:justify;">\n	- Tôi đã tìm ra một phương thức kinh doanh lý tưởng, không bao giờ bị lỗ cả. Tôi kiếm chác bằng cách bán đi một trong những đồ vật cá nhân của tôi.</p>\n<p style="text-align:justify;">\n	Câu chuyện nhăng cuội của anh ta không lấy gì làm hấp dẫn đối với tôi lắm. Sở dĩ tôi đi cùng anh ta là bởi vì chúng tôi đi cùng đường. Vả lại ban đêm đi một mình không phải bao giờ cũng an toàn, nhất là ở những vùng vắng người. Tuy nhiên tôi cũng không thể không bắt chuyện:</p>\n<p style="text-align:justify;">\n	- Anh muốn nói rằng anh có rất nhiều đồ dùng cá nhân lắm à?</p>\n<p style="text-align:justify;">\n	- Không, tôi bao giờ cũng chỉ bán một thứ.</p>\n<p style="text-align:justify;">\n	- Chắc anh đã bán nó một lần và với số tiền thu được anh đã...</p>\n<p style="text-align:justify;">\n	- Không tôi bán nó không ngừng.</p>\n<p style="text-align:justify;">\n	- Anh làm thế nào mà có thể bán một thứ hàng đến mấy lần?</p>\n<p style="text-align:justify;">\n	- Thì chính tôi cũng không hiểu mà lại. Nhưng đó là một sự thật hoàn toàn. Tôi chào hàng, người ta trả tiền cho tôi còn hàng thì không lấy.</p>\n<p style="text-align:justify;">\n	- Lạ nhỉ - tôi hỏi - đó là thứ hàng gì vậy?</p>\n<p style="text-align:justify;">\n	- Đó là khẩu súng lục của tôi. Cho dù tôi chào hàng với bất cứ ai thì người mua lập tức đưa tôi tiền và điều kỳ lạ nhất là họ không lấy súng. Đôi khi tôi cố trao hàng cho họ bằng cách đi theo họ nhưng không ăn thua gì. Tất cả những khách hàng đều đi rất nhanh, có thể nói là họ chạy một mạch.</p>\n<p style="text-align:justify;">\n	- Chắc là anh quảng cáo món hàng, ca ngợi chất lượng của nó, nào là nó dễ sử dụng, nào là đường ngắm chính xác?</p>\n<p style="text-align:justify;">\n	- Tôi không kịp làm điều đó. Thông thường tôi nói rằng tôi không phải là người bán hàng chuyên nghiệp, đó là sự thật, tôi không lừa ai bao giờ, và chỉ trong hoàn cảnh túng bấn mới buộc tôi phải giã từ thứ đồ dùng này. Tôi bắt đầu bằng việc giơ cho khách hàng xem khẩu súng và nói: “Tôi nghèo khó! Tôi cần một ít tiền...”. Tôi chưa kịp nói xong thì khách hàng đã trả tiền và bỏ chạy. Tôi rượt theo sau để trao hàng và gọi toáng lên: “Ông kia hãy dừng lại!” nhưng chân anh ta cứ như mọc cánh vậy!</p>\n<p style="text-align:justify;">\n	- Lạ thật! Tại sao lại thế nhỉ?</p>\n<p style="text-align:justify;">\n	- Tôi cho rằng sở dĩ tôi thắng đậm là bởi vì tôi đã tuân thủ nghiêm ngặt những qui luật buôn bán.</p>\n<p style="text-align:justify;">\n	- Những qui luật gì vậy?</p>\n<p style="text-align:justify;">\n	- Đó là chỉ chào hàng ở nơi và lúc mà nó cần thiết nhất. Ở đâu và lúc nào thì khẩu súng lục cần nhất? Ở nơi mà cần bảo vệ mình. Chẳng hạn, ban đêm ở trên đường phố vắng. Ai là kẻ cần đến súng? Người không có vũ khí. Thật là ngu xuẩn nếu đem súng lục đi mời kẻ có trong tay khẩu tiểu liên tự động!</p>\n<p style="text-align:justify;">\n	- Tất nhiên rồi.</p>\n<p style="text-align:justify;">\n	- Vậy là ban đêm tôi đi đến một vùng hẻo lánh và nếu tôi nhìn thấy một người đi bộ một mình có vẻ nhút nhát, thì tôi liền mời anh ta mua súng lục. Đôi khi để tăng thêm sức thuyết phục, tôi nói thêm: “Ông hãy xem đây, súng đã lên đạn rồi. Chỉ cần bấm cò là cái chết đến ngay tức khắc!”</p>\n<p style="text-align:justify;">\n	- Còn người đi đường thì sao?</p>\n<p style="text-align:justify;">\n	- Thật kỳ cục, anh ta trả tôi một số tiền nhiều hơn giá một khẩu súng, có người trả bằng tiền, có người trả bằng hiện vật. Có người sau khi trao cho tôi số tiền lại còn quẳng cho tôi cái áo khoác rồi cắm cổ chạy mất. Nhưng tôi không dừng lại ở đây. Tôi suy nghĩ xem ai còn cần đến súng lục nũa không? Tỷ dụ như người vừa lĩnh ở ngân hàng một số tiền lớn. Tất nhiên anh ta không cần bảo vệ ở phố xá đông người. Bởi vậy tôi lẽo đẽo theo anh ta một hồi lâu và đưa hàng ra chào ở một góc phố khuất nẻo, nhưng ngay cả trong trường hợp này, đáng lẽ phải tận dụng sự phục dịch tận tâm của tôi và mua thứ hàng hữu ích mà rẻ tiền đó, thì anh ta thậm chí chẳng cần đợi đến lúc tôi nói giá, liền trao ngay cho tôi tất cả gói giấy bạc.</p>\n<p style="text-align:justify;">\n	- Và số tiền đó tất nhiên là cao hơn cái giá khiêm tốn của khẩu súng lục chứ?</p>\n<p style="text-align:justify;">\n	- Lẽ cố nhiên. Nhưng xin ông hãy nghe tiếp. Do có hiện tượng kỳ quặc như vậy nên tôi bèn đến cửa hàng súng để kiểm tra xem trong khẩu súng của tôi có tàng trữ giá trị gì đặc biệt không? Tôi tới đó trong lúc chủ hiệu đang thiu thiu ngủ sau quầy hàng, tôi lấy súng ra và bắt đầu giải thích: “Tôi bị nghèo túng!”. Thế là y liền đưa toàn bộ số tiền trong két. Tại các cửa hàng khác cũng đều xảy ra cảnh tương tự.</p>\n<p style="text-align:justify;">\n	Hai chúng tôi đi ngang qua những ngôi nhà cuối cùng trên phố vắng và đến một khúc quanh.</p>\n<p style="text-align:justify;">\n	- Tôi chả phát hiện ra được những giá trị tiềm tàng nào trong khẩu súng của tôi. Có thể nào ông giúp tôi làm việc đó được chăng? - Anh ta rút khẩu súng ra - Như ông thấy đấy, khẩu súng rất hoàn hảo. Chỉ cần khẽ bấm cò.</p>\n<p style="text-align:justify;">\n	- Tôi hiểu! - Tôi kêu lên và quẳng cho anh ta số tiền rồi ù té chạy, tai vẫn còn nghe thấy tiếng gọi của anh ta ở phía sau lưng. Sau đó tôi ngoái nhìn lại thì anh ta đã biến mất hút sau khi đút vào túi cái ví của tôi và khẩu súng mà anh ta không thể nào bứt ra được.</p>\n<p style="text-align:justify;">\n	Mọi người có thể cho rằng tôi đã vội vàng đưa cho anh ta cái ví, cho nên không còn xu nào dính túi chăng? Không hề. Tôi còn định thưởng cho mình là vì đã tiếp thu được hệ thống của anh ta. Cũng như anh ta, tôi có thể bán một thứ đồ dùng cá nhân nào đó và có thể kiếm được ít tiền mà vẫn giữ được đồ vật ấy. Tôi không có súng lục nhưng có thể thử với thứ khác, với chiếc đồng hồ chẳng hạn.</p>\n<p style="text-align:justify;">\n	Quả thật tôi có chiếc đồng hồ mạ vàng tuyệt đẹp của Thụy Sĩ mà một người đi đường nào đó có thể thích thú.</p>\n<p style="text-align:justify;">\n	Ở một góc phố tôi nhìn thấy một người ăn mặc xuềnh xoàng và ắt hẳn y không có chiếc đồng hồ như thế này. Tôi mạnh bạo đến gần y và chìa chiếc đồng hồ ra.</p>\n<p style="text-align:justify;">\n	- Ông có cần đồng hồ không ạ? - Tôi hỏi - Chiếc đồng hồ này mạ vàng, mác Ômêga có kim giây và mười tám chân kính bằng hồng ngọc...</p>\n<p style="text-align:justify;">\n	Tôi chưa kịp nói hết lời. Thay vì trả tiền tôi và bỏ chạy sau khi để lại món hàng trên tay tôi, hắn chộp luôn cái đồng hồ rồi cắm cổ chạy mà chẳng giả xu nào.</p>\n<p style="text-align:justify;">\n	Ôi cái bí quyết của nghề kinh doanh!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'bi-quyet-kinh-doanh', 'sung, dan da len nong, dong ho vang', NULL, 84, '2012-01-29 06:23:55', 63, 0, NULL, NULL, 1, 3),
(23, 'danghaphoi3.jpg', 'Bệnh nghề nghiệp của giám đốc', '', '', '<p style="text-align:justify;">\n	Giám đốc một công ty quảng cáo đang lâm bệnh nặng, trước phút lâm chung, ông ta thiều thào đọc di chúc cho luật sư:</p>\n', '', '', '<p style="text-align:justify;">\n	Giám đốc một công ty quảng cáo đang lâm bệnh rất nặng, trước phút lâm chung, ông ta thều thào đọc di chúc cho luật sư:</p>\n<p style="text-align:justify;">\n	- Hãy chia cho các nhân viên đã làm việc cho công ty trên 20 năm mỗi người 500.000.000 đồng.</p>\n<p style="text-align:justify;">\n	- Nhưng công ty của ông chỉ mới thành lập có 15 năm thôi mà? Luật sư ngạc nhiên!</p>\n<p style="text-align:justify;">\n	- Tôi biết điều đó. Nhưng ông hãy để tôi được... quảng cáo cho công ty của mình trước khi chết chứ!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'benh-nghe-nghiep-cua-giam-doc', 'luat su, quang cao, nhan vien, giam doc', NULL, 108, '2012-02-01 13:03:36', 64, 0, NULL, NULL, 1, 3),
(24, '1318383695_doctor2.jpg', 'Cần quái gì thuốc của ông', '', '', '<p>\n	Một giám đốc doanh nghiệp than phiền với bác sĩ nam học:</p>\n', '', '', '<p>\n	Một giám đốc doanh nghiệp than phiền với bác sĩ nam học:</p>\n<p>\n	- Sao dạo này tôi "yếu" quá, chẳng hứng thú gì "chuyện ấy" với bà xã cả.</p>\n<p>\n	- Được rồi, nếu vậy ông hãy dùng thử liều thuốc này, nó sẽ công hiệu sau hai ngày.</p>\n<p>\n	- Nhưng hai ngày nữa vợ tôi vắng nhà, làm thế nào để biết được tác dụng của thuốc?</p>\n<p>\n	- Chắc ông có nữ thư ký chứ?</p>\n<p>\n	- Ông định chơi khăm tôi à, cô ta mà đồng ý thì tôi cần quái gì đến thuốc của ông!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'can-quai-gi-thuoc-cua-ong', 'nam hoc, bac si, thu ky, giam doc', NULL, 99, '2012-02-01 13:22:04', 66, 0, NULL, NULL, 1, 3),
(25, 'chi_tai_con_ga_trong_2.jpg', 'Gà trống phá đám', '', '', '<p style="text-align:justify;">\n	Giám đốc một công ty nọ đưa vợ đi xem trang trại chăn nuôi gà của người bạn. Chủ trang trại chỉ một con gà trống rất đẹp và giới thiệu:</p>\n', '', '', '<p style="text-align:justify;">\n	Giám đốc một công ty nọ đưa vợ đi xem trang trại chăn nuôi gà của người bạn. Chủ trang trại chỉ một con gà trống rất đẹp và giới thiệu: "đây là giống gà cao sản, nó có thể đạp mái liên tục hơn chục lần mỗi ngày"</p>\n<p style="text-align:justify;">\n	Người vợ vừa dùng cùi chỏ thúc vào hông chồng vừa hỏi giọng chê bai:</p>\n<p style="text-align:justify;">\n	- Anh nghe rõ chứ?</p>\n<p style="text-align:justify;">\n	Anh này liền kéo vợ lại gần ông chủ trang trại vừa hỏi vừa nheo mắt ra hiệu:</p>\n<p style="text-align:justify;">\n	- Nó làm được việc ấy với 10 con gà mái khác nhau phải không?</p>\n<p style="text-align:justify;">\n	- Đương nhiên rồi!</p>\n<p style="text-align:justify;">\n	Người chồng quay sang vợ nói:</p>\n<p style="text-align:justify;">\n	- Đó, em nghe rõ chứ?</p>\n<p style="text-align:justify;">\n	Nói xong, anh ta vừa nhìn con gà trống một cách ấm ức vừa nghĩ trong bụng "đồ phá đám".</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'ga-trong-pha-dam', 'ga trong, giam doc, chu trang trai', NULL, 117, '2012-02-01 13:39:50', 67, 0, NULL, NULL, 1, 3),
(27, 'images.jpg', 'Lâu lắm rồi', '', '', '<p style="text-align:justify;">\n	Trong buổi dạ hội, nhà kinh doanh hoa quả khiêu vũ với một quý bà mặc áo hở ngực rộng.</p>\n', '', '', '<p style="text-align:justify;">\n	Trong buổi dạ hội, một nhà buôn hoa quả khiêu vũ với một quý bà mặc áo hở ngực.</p>\n<p style="text-align:justify;">\n	Thấy bạn nhảy cứ nhìn chằm chằm vào ngực mình, bà nọ mỉm cười đưa đẩy:</p>\n<p style="text-align:justify;">\n	- Chắc đã lâu ông chưa có dịp thấy... phải không?</p>\n<p style="text-align:justify;">\n	- Phải! Lâu lắm rồi, từ khi tôi còn... buôn đu đủ.</p>\n<p style="text-align:justify;">\n	- !!!!!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'lau-lam-roi', 'hoa quà, kinh doanh, ho nguc', NULL, 77, '2012-02-04 13:49:29', 26, 0, NULL, NULL, 1, 3),
(28, 'tiengia.jpg', 'Cạnh tranh với chính phủ', '', '', '<p style="text-align:justify;">\n	Thanh tra liên bang ở Mỹ đi thị sát nhà tù, họ hỏi một tù nhân: "Anh bị tù vì tội gì?"</p>\n', '', '', '<p style="text-align:justify;">\n	Thanh tra liên bang ở Mỹ đi thị sát nhà tù, họ hỏi một tù nhân: "Anh bị tù vì tội gì?"</p>\n<p style="text-align:justify;">\n	- Dạ, tội cạnh tranh.</p>\n<p style="text-align:justify;">\n	- Nói láo! Nước ta là một nước dân chủ, có nền kinh tế tự do cạnh tranh nên không ai có thể bị tù vì tội đó.</p>\n<p style="text-align:justify;">\n	- Thưa, rõ ràng tôi bị xử tội đó. Tôi đã cạnh tranh với chính phủ trong việc sản xuất tiền!</p>\n<p style="text-align:justify;">\n	- !!!!!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'canh-tranh-voi-chinh-phu', 'di tu, canh tranh, san xuat tien', NULL, 92, '2012-02-04 14:02:29', 48, 0, NULL, NULL, 1, 3);
INSERT INTO `dos_module_entertainment` (`record_id`, `pic_thumb`, `title`, `titleen`, `titlefr`, `preview`, `previewen`, `previewfr`, `content`, `contenten`, `contentfr`, `tag`, `description`, `author`, `hits`, `postdate`, `record_order`, `record_type`, `extra_field1`, `extra_field2`, `enable`, `dos_module_item_cat_cat_id`) VALUES
(29, 'osin.jpg', 'Nhật ký oshin thời @ (Phần 1)', '', '', '<p style="text-align:justify;">\n	Chàng lại về muộn. Chàng bỏ bữa tối. "Nó" lại chửi chàng.</p>\n<p style="text-align:justify;">\n	<em>Ngày... tháng... năm...</em></p>\n', '', '', '<p style="text-align:justify;">\n	Chàng lại về muộn. Chàng bỏ bữa tối. "Nó" lại chửi chàng.</p>\n<p style="text-align:justify;">\n	<em>Ngày... tháng... năm...</em></p>\n<p style="text-align:justify;">\n	Chàng lại về muộn. Chàng bỏ bữa tối. "Nó" lại chửi chàng.</p>\n<p style="text-align:justify;">\n	<em>Ngày... tháng... năm...</em></p>\n<p style="text-align:justify;">\n	Nấu món chàng thích nhất. Thế mà "nó" giành ăn hết. Lại còn khen mình nấu ngon nữa. Đểu thế. Tội chàng quá! Mai "nó" đi công tác, mình cho chàng ăn "phở".</p>\n<p style="text-align:justify;">\n	<em>Ngày... tháng... năm...</em></p>\n<p style="text-align:justify;">\n	Đi đăng ký tạm trú. Mục "Quan hệ với chủ hộ", mình điền: 3 lần/tuần. Chúng nó trợn tròn mắt. Nhiều hay ít nhỉ?</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n<p>\n	<span style="text-decoration:underline;"><a href="http://khoisudoanhnghiep.vn/giai-tri/nhat-ky-oshin-thoi-phan-2.html"><span style="color:#0000cd;">Nhật ký oshin thời @ (Phần 2)</span></a></span></p>\n', '', '', 'nhat-ky-oshin-thoi-phan-1', 'an pho, quan he voi chu ho, oshin', NULL, 97, '2012-02-05 11:58:49', 28, 0, NULL, NULL, 1, 3),
(30, 'thuky.jpg', 'Dã ngoại với sếp', '', '', '<p style="text-align:justify;">\n	Một cô thư ký kể cho bạn gái nghe về đợt dã ngoại của công ty mình trong lúc chat...</p>\n', '', '', '<p style="text-align:justify;">\n	Một cô thư ký kể cho bạn gái nghe về đợt dã ngoại của công ty mình trong luc chat...</p>\n<p style="text-align:justify;">\n	- Cậu tưởng tượng được không, chúng tớ đang ngủ trong vựa cỏ khô thì một cơn dông ào ào ập tới, gió thổi mạnh đến nỗi mái kho đổ sập xuống.</p>\n<p style="text-align:justify;">\n	- Thế mà cậu không chết bẹp à?</p>\n<p style="text-align:justify;">\n	- Không sao, cái mái ấy đổ cả xuống lưng ông sếp của tớ! Hú hồn!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'da-ngoai-voi-sep', 'sep, da ngoai, thu ky', NULL, 84, '2012-02-05 12:08:22', 29, 0, NULL, NULL, 1, 3),
(31, 'teeth.jpg', 'Răng của mình', '', '', '<p>\n	Nhìn cô gái cười khoe hàm răng trắng đẹp quảng cáo kem đánh răng...</p>\n<p>\n	Một anh chàng khen:</p>\n', '', '', '<p>\n	Nhìn cô gái cười khoe hàm răng trắng đẹp quảng cáo kem đánh răng...</p>\n<p>\n	Một anh chàng khen:</p>\n<p>\n	- Chà! Hàm răng trắng đẹp tuyệt trần!</p>\n<p>\n	- Răng của mình đấy...</p>\n<p>\n	- Khoác!</p>\n<p>\n	- Thì chính mình trồng cho cô ta mà!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'rang-cua-minh', 'kem danh rang, quang cao', NULL, 93, '2012-02-05 12:22:12', 30, 0, NULL, NULL, 1, 3),
(32, 'images4.jpg', 'Công dân tốt', '', '', '<p>\n	Sếp chở nữ thư ký đi "công tác" bằng xe ô tô, dọc đường xe chết máy.</p>\n<p>\n	Sếp nói:</p>\n', '', '', '<p style="text-align:justify;">\n	Sếp chở nữ thư ký đi "công tác" bằng xe ô tô. Khi đi ngang đoạn đường vắng, bổng dưng xe chết máy.</p>\n<p style="text-align:justify;">\n	Sếp nói:</p>\n<p style="text-align:justify;">\n	- Là sếp, đáng lẽ tôi phải giao ngay việc này cho trung tâm cứu hộ, nhưng là đấng nam nhi nên tôi phải tự mình sửa chữa xe. Nói xong, ông ta bước ra khỏi xe và chui xuống gầm xe.</p>\n<p style="text-align:justify;">\n	- Là một người phụ nữ, đáng lẽ em phải ngồi trên xe, nhưng với tư cách là thư ký của sếp, em cần phải ở cạnh sếp. Cô thư ký nói và sau đó cũng bước ra khỏi xe và chui xuống gầm xe.</p>\n<p style="text-align:justify;">\n	Nửa tiếng sau, một người đàn ông đi ngang qua nói:</p>\n<p style="text-align:justify;">\n	- Là một người lịch sự, lẽ ra tôi không nên làm phiền hai người, nhưng là một công dân tốt, tôi cần phải báo cho hai người biết "Chiếc xe vừa mới chạy mất rồi".</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'cong-dan-tot', 'sep, thu ky, cong tac, xe o to', NULL, 87, '2012-02-15 13:25:21', 31, 0, NULL, NULL, 1, 3),
(33, 'mac_cung_cham_2.jpg', 'Mặc quá lâu', '', '', '<p style="text-align:justify;">\n	Vợ dò hỏi ông chồng giám đốc: "Này anh, em nghe nói anh mới tuyển cô thư ký trẻ, xinh lắm phải không?"</p>\n', '', '', '<p style="text-align:justify;">\n	Vợ dò hỏi ông chồng giám đốc: "Này anh, em nghe nói anh mới tuyển cô thư ký trẻ, xinh lắm phải không?"</p>\n<p style="text-align:justify;">\n	- Tất nhiên không thể bằng em.</p>\n<p style="text-align:justify;">\n	- Thế cô ấy ăn mặc thế nào?</p>\n<p style="text-align:justify;">\n	- Quá lâu.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'mac-qua-lau', 'vo, giam doc, thu ky, rat cham', NULL, 78, '2012-02-15 13:30:10', 32, 0, NULL, NULL, 1, 3),
(34, '4.jpg', 'Quên cạo râu', '', '', '<p style="text-align:justify;">\n	Sếp: Tôi đọc văn bản rõ ràng như vậy mà cô vẫn đánh sai chính tả đến hàng tá lỗi.</p>\n', '', '', '<p style="text-align:justify;">\n	Sếp: Tôi đọc văn bản rõ ràng như vậy mà cô vẩn đánh sai chính tả đến hàng tá lỗi.</p>\n<p style="text-align:justify;">\n	Thư ký: Vâng, em cũng nghe rõ mà. Có điều khi gõ bàn phím em nhột quá nên mất tập trung.</p>\n<p style="text-align:justify;">\n	Sếp: Gỗ bàn phím sao lại nhột?</p>\n<p style="text-align:justify;">\n	Thư ký: Dạ, hình như sáng nay sếp... quên cạo râu.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'quen-cao-rau', 'sep, thu ky, quen cao rau', NULL, 106, '2012-02-15 13:32:25', 70, 0, NULL, NULL, 1, 3),
(35, 'osin4.jpg', 'Nhật ký oshin thời @ (Phần 2)', '', '', '<p>\n	<em>Ngày… tháng… năm…</em><br />\n	"Nó" lại chửi chàng.</p>\n', '', '', '<p>\n	<span style="text-decoration:underline;"><a href="http://khoisudoanhnghiep.vn/giai-tri/nhat-ky-oshin-thoi-phan-1.html"><span style="color:#0000cd;">Nhật ký oshin thời @ (Phần 1)</span></a></span></p>\n<p>\n	<em>Ngày… tháng… năm…</em><br />\n	"Nó" lại chửi chàng.<br />\n	Chàng đang tắm...<br />\n	Đến lượt "nó" tắm.<br />\n	Chàng tranh thủ...<br />\n	Ước gì cả ngày "nó" ở trong nhà tắm!</p>\n<p>\n	<em>Ngày... tháng... năm...</em><br />\n	Mấy hôm nay mình thấy buồn nôn. Hê hê, sướng thế! Biết đâu...<br />\n	"Nó" mà biết thì...<br />\n	Ơ, nhưng mà tháng trước mình lại rập rình với thằng xế lô...</p>\n<p>\n	<em>Ngày… tháng… năm…</em><br />\n	Đêm nay "nó" lại oánh chàng.<br />\n	Ngày mai bà mua thuốc chuột...<br />\n	Cho "nó" tèo!</p>\n<p>\n	<em>Ngày... tháng... năm...</em><br />\n	Con gái "nó" hỗn với mình.<br />\n	Bảo mình là đồ oshin.<br />\n	Tức thế chứ! Bà là, là, là...<br />\n	Sau này bà không cho con bà gọi mày bằng chị đâu nhá.<br />\n	Không có thứ chị em nào với mày nhá!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nhat-ky-oshin-thoi-phan-2', 'oshin, no', NULL, 86, '2012-02-15 13:45:44', 68, 0, NULL, NULL, 1, 3),
(36, 'mac_cung_cham_27.jpg', 'Cải tổ bộ máy', '', '', '<p>\n	Một công ty liên tục làm ăn thua lỗ, giám đốc công ty quyết định cải tổ bộ máy bằng cách sa thải bớt nhân viên.</p>\n', '', '', '<p>\n	Một công ty liên tục làm ăn thua lỗ, giám đốc quyết định cải tổ bộ máy bằng cách sa thải bớt nhân viên.</p>\n<p>\n	Ông ta bàn với vợ:</p>\n<p>\n	- Em à, nếu em cố gắng nấu ăn tàm tạm cho nhân viên thì anh có thể sa thải bà cấp dưỡng.</p>\n<p>\n	Bà vợ gật đầu đồng ý và nói với chồng:</p>\n<p>\n	- Còn nếu anh chịu khó đêm đêm ngủ với em thì ta có thể cho cậu lái xe nghỉ việc.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'cai-to-bo-may', 'cai to, sa thai, giam doc', NULL, 79, '2012-02-16 13:14:11', 43, 0, NULL, NULL, 1, 3),
(37, 'index.jpg', 'Trả đủa', '', '', '<p style="text-align:justify;">\n	Trong cuộc phỏng vấn kế toán tại một công ty, sếp tổng hỏi cô gái xinh đẹp mặc váy ngắn.</p>\n', '', '', '<p style="text-align:justify;">\n	Trong cuộc phỏng vấn kế toán tại một công ty, sếp tổng hỏi cô gái xinh đẹp mặc váy ngắn.</p>\n<p style="text-align:justify;">\n	- Cô đã không trả lời được 2 câu đầu tiên tôi hỏi rồi, bây giờ là cơ hội cuối cùng của cô, nếu cô trả lời được thì tôi sẽ nhận cô vào làm, nếu không thì đành phải chia tay cô ở đây.</p>\n<p style="text-align:justify;">\n	Nếu tôi viết số 1 lên cả đùi trái và đùi phải của cô thì sẽ là số mấy.</p>\n<p style="text-align:justify;">\n	Cô gái trả lời:</p>\n<p style="text-align:justify;">\n	- Số 11 thưa ông.</p>\n<p style="text-align:justify;">\n	- Cô thật là ngốc, đến đọc số mà cũng không xong thì sao mà làm kế toán được. Phải là 101 mới đúng.</p>\n<p style="text-align:justify;">\n	Cô gái ngượng đỏ mặt, tuy nhiên trước khi ra khỏi phòng cô vẫn không phục bèn hỏi lại sếp tổng:</p>\n<p style="text-align:justify;">\n	- Ngài nói đúng, đó là số 101, nhưng tôi không ngốc. Nếu số 1 đó được viết lên hai đùi của ngài thì ngài đọc đó là số mấy?</p>\n<p style="text-align:justify;">\n	Sếp cười bảo:</p>\n<p style="text-align:justify;">\n	- Vậy mà cô cũng hỏi, tất nhiên là 111 rồi.</p>\n<p style="text-align:justify;">\n	Cô gái cười lớn:</p>\n<p style="text-align:justify;">\n	- Không phải 111 mà là 1,1. Hóa ra ngài cũng chẳng hơn gì tôi.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'tra-dua', 'sep, mau 35', NULL, 90, '2012-02-16 13:36:34', 36, 0, NULL, NULL, 1, 3),
(38, 'b4.jpg', 'Dễ thế mà bố không trả lời được', '', '', '<p style="text-align:justify;">\n	"Cơ quan nào của phụ nữ to nhất?/Lông, tóc của phụ nữ ở đâu xoăn nhất?" là những câu hỏi dành cho cặp cha con lọt vào chung kết trong cuộc thi nhân ngày các bà mẹ.</p>\n', '', '', '<p style="text-align:justify;">\n	Nhân ngày hội các bà mẹ, công ty tổ chức một cuộc thi với sự tham gia của các cặp cha con. Ban tổ chức nêu ba câu hỏi cho cặp lọt vào chung kết. Hai cha con trả lời bằng cách viết vào giấy và gửi lại, sau mỗi câu thì ban tổ chức chỉ công bố kết quả đúng - sai.</p>\n<p style="text-align:justify;">\n	Câu 1: Cơ quan nào của phụ nữ to nhất?</p>\n<p style="text-align:justify;">\n	Kết quả: Bố trả lời sai, con trai - đúng.</p>\n<p style="text-align:justify;">\n	Câu 2: Lông, tóc của phụ nữ ở đâu nào xoăn nhất?</p>\n<p style="text-align:justify;">\n	Kết quả: Bố - sai, con - đúng.</p>\n<p style="text-align:justify;">\n	Câu 3: Mẹ thích nhất nắm cái gì của bố?</p>\n<p style="text-align:justify;">\n	Kết quả: Bố - sai, con - đúng.</p>\n<p style="text-align:justify;">\n	Sau khi cậu con trai nhận phần thưởng về, người cha giọng hoảng hốt hỏi:</p>\n<p style="text-align:justify;">\n	- Đáp án của con là gì?</p>\n<p style="text-align:justify;">\n	- Câu trả lời của con là: Cơ quan to nhất của phụ nữ là Hội Liên hiệp Phụ nữ; lông, tóc của phụ nữ châu Phi xoăn nhất và mẹ thích nhất là nắm được ví của bố.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'de-the-ma-bo-khong-tra-loi-duoc', 'cong ty, bo, con, ket qua, dap an', NULL, 85, '2012-02-16 13:43:15', 37, 0, NULL, NULL, 1, 3),
(39, 'index2.jpg', 'Không dám nhận', '', '', '<p style="text-align:justify;">\n	Công ty nọ tổ chức ngày hội gia đình, có mặt đông đủ nhân viên và vợ/chồng của họ. Trong khi phát biểu về nạn bạo hành gia đình, vị giám đốc tuổi lục tuần ngừng lại và hỏi những nhân viên nam:<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Công ty nọ tổ chức ngày hội gia đình, có mặt đông đủ nhân viên và vợ/chồng của họ. Trong khi phát biểu về nạn bạo hành gia đình, vị giám đốc tuổi lục tuần ngừng lại và hỏi những nhân viên nam:</p>\n<p style="text-align:justify;">\n	- Ở đây ai đã từng đánh nhau với vợ mình hãy đứng dậy?</p>\n<p style="text-align:justify;">\n	Tất cả nhân viên nam đều đứng dậy, chỉ một người tuổi trạc ba mươi vẫn ngồi yên tại chỗ một mình. Giám đốc liền bước xuống bục, tiến lại gần chỗ anh ta và nói với giọng biểu dương:</p>\n<p style="text-align:justify;">\n	- Tôi đã trải qua gần hết cuộc đời nên kinh nghiệm gia đình nhiều hơn các bạn. Các bạn hãy nhớ chữ nhẫn mà ông bà ta dạy, một câu nhịn chín câu lành. Ở công ty ta, anh Minh đây là người thấm nhuần lời dạy ấy và là người duy nhất chưa từng đánh nhau với vợ, nên anh rất đáng được biểu dương, rất đáng được anh em ta học tập.</p>\n<p style="text-align:justify;">\n	Mọi người vỗ tay tán thưởng. Riêng người đàn ông ngồi im lặng trong giây lát rồi phát biểu với giọng bùi ngùi:</p>\n<p style="text-align:justify;">\n	- Thưa sếp, tôi không dám nhận lời khen của sếp đâu.</p>\n<p style="text-align:justify;">\n	- Tại sao vậy? Vị giám đốc già hỏi.</p>\n<p style="text-align:justify;">\n	- Bởi vì, hôm qua tôi vừa đánh nhau với vợ và bị vợ đánh què chân, không thể đứng dậy được, người đàn ông ấp úng.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'khong-dam-nhan', 'danh nhau voi vo, bi vo danh', NULL, 79, '2012-02-20 14:32:38', 38, 0, NULL, NULL, 1, 3),
(40, 'images7.jpg', 'Lãnh lương sớm', '', '', '<p style="text-align:justify;">\n	Một nhân viên nói với đồng nghiệp: "Có lương rồi, chiều nay cậu đi rút tiền đi"</p>\n', '', '', '<p style="text-align:justify;">\n	Một nhân viên nói với đồng nghiệp: "Có lương rồi, chiều nay cậu đi rút tiền đi"</p>\n<p style="text-align:justify;">\n	Sao tháng này có lương sớm thế, mới mùng mười đã có lương rồi? Mọi khi đến hai mươi mới có lương, công ty mình đổi lịch trả lương rồi à? Người kia ngạc nhiên.</p>\n<p style="text-align:justify;">\n	Ồ, không phải đổi lịch trả lương đâu. Chẳng qua là vì ngày 12 sếp tổ chức đám cưới cho con.</p>\n<p style="text-align:justify;">\n	Cậu sẽ sớm nhận được thiệp mời của sếp thôi mà.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'lanh-luong-som', 'lanh luong som, lich tra luong', NULL, 83, '2012-02-25 12:07:28', 39, 0, NULL, NULL, 1, 3),
(41, 'raidinh1.jpg', 'Oan cho tiệm em lắm', '', '', '<p style="text-align:justify;">\n	Một người bị cán đinh của bọn đinh tặc khi đang đi xe máy vừa dẫn xe vào tiệm sửa xe vừa nói giọng bực tức: "Bọn đinh tặc bất lương này, sớm muộn gì rồi chúng cũng gặp quả báo"<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một người bị cán đinh của bọn đinh tặc khi đang đi xe máy vừa dẫn xe vào tiệm sửa xe vừa nói giọng bực tức: "Bọn đinh tặc bất lương này sớm muộn gì rồi chúng cũng gặp quả báo".</p>\n<p style="text-align:justify;">\n	Tay chủ tiệm nghe thấy vội vàng phân bua khi vừa tháo xong đinh cho khách hàng:</p>\n<p style="text-align:justify;">\n	"Đinh của tiệm em rải là đinh móc, còn đinh xe anh cán là đinh thoi. Anh nói thế rất oan cho tiệm của em đó".</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'oan-cho-tiem-em-lam', 'can dinh, tiem sua xe', NULL, 80, '2012-02-25 12:12:10', 40, 0, NULL, NULL, 1, 3),
(42, 'index1.jpg', 'Người khắc phục hậu quả', '', '', '<p style="text-align:justify;">\n	Một người đàn ông bế hai đứa trẻ trên tay, đứng đợi ở ga tàu điện ngầm.</p>\n', '', '', '<p style="text-align:justify;">\n	Một người đàn ông bế hai đứa trẻ trên tay, đứng đợi ở ga tàu điện ngầm.</p>\n<p style="text-align:justify;">\n	Một phụ nữ nhìn thấy hai đứa nhỏ xinh xắn liền gợi chuyện:</p>\n<p style="text-align:justify;">\n	- Ồ, bọn trẻ xinh quá, tên chúng là gì vậy hả ông?</p>\n<p style="text-align:justify;">\n	- Tôi không biết! - Người đàn ông giận dữ trả lời.</p>\n<p style="text-align:justify;">\n	- Vậy chúng được mấy tháng tuổi rồi hả ông!</p>\n<p style="text-align:justify;">\n	- Tôi không biết! - Người đàn ông quắc mắt trả lời.</p>\n<p style="text-align:justify;">\n	Đến lúc này người phụ nữ cũng giận dữ chẳng kém, bà lớn tiếng trách mắng:</p>\n<p style="text-align:justify;">\n	- Ông làm cha kiểu gì vậy?</p>\n<p style="text-align:justify;">\n	- Ồ, tôi đâu phải là cha chúng. Tôi chỉ là nhân viên thương mại của công ty kinh doanh bao cao su, còn đây là hai lời than phiền của khách hàng gửi trả cho công ty chúng tôi đấy chứ.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'nguoi-khac-phuc-hau-qua', 'hau qua, bao cao su', NULL, 72, '2012-02-25 12:50:35', 41, 0, NULL, NULL, 1, 3),
(43, 'index25.jpg', 'Xúc xích và heo', '', '', '<p style="text-align:justify;">\n	Con: Bố ơi, cô giáo con nói ở nước ngoài có một nhà máy hay lắm. Công nhân của nhà máy chỉ cần cho cả con heo vào đầu này là đầu kia cứ việc lấy xúc xích ra.</p>\n', '', '', '<p style="text-align:justify;">\n	Con: Bố ơi, cô giáo con nói ở nước ngoài có một nhà máy hay lắm. Công nhân của nhà máy chỉ cần cho cả con heo vào đầu này là đầu kia cứ việc lấy xúc xích ra.</p>\n<p style="text-align:justify;">\n	Bố: Ồ, thế thì cũng như nhà máy của bố thôi con trai à. Có điều nhà máy của bố thì ngược lại, cứ đầu vào có xúc xích là đầu ra có một chú heo con.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'xuc-xich-va-heo', 'heo, xuc xich, bo, con', NULL, 84, '2012-02-28 09:09:03', 42, 0, NULL, NULL, 1, 3),
(44, 'images5.jpg', 'Ăn cả vỏ', '', '', '<p style="text-align:justify;">\n	Một nữ doanh nhân đi khám sức khỏe định kỳ ở một phòng mạch</p>\n<p style="text-align:justify;">\n	- Sao da tôi dạo này không được sáng và đẹp như trước, trong khi tôi toàn sử dụng mỹ phẩm đắt tiền? Nữ doanh nhân hỏi bác sĩ.</p>\n', '', '', '<p style="text-align:justify;">\n	Một nữ doanh nhân đi khám sức khỏe định kỳ ở một phòng mạch</p>\n<p style="text-align:justify;">\n	- Sao da tôi dạo này không được sáng và đẹp như trước, trong khi tôi toàn sử dụng mỹ phẩm đắt tiền? Nữ doanh nhân hỏi bác sĩ.</p>\n<p style="text-align:justify;">\n	- Bà vừa uống quá ít nước vừa ăn không đủ các chất xơ và vitamine. Bác sĩ trả lời rồi dặn tiếp:</p>\n<p style="text-align:justify;">\n	- Tôi sẽ cho bà một số thuốc, bà nhớ uống trước khi ăn cơm. Ngoài ra, bà cần phải uống nhiều nước và ăn nhiều trái cây, nếu ăn cả vỏ sẽ tốt hơn cho da đấy. Bà nhớ làm đúng lời tôi dặn, ba tuần sau trở lại đây tái khám.</p>\n<p style="text-align:justify;">\n	- Vâng, nữ doanh nhân trả lời.</p>\n<p style="text-align:justify;">\n	Ba tuần sau</p>\n<p style="text-align:justify;">\n	- Da bà hiện tại đã cải thiện nhiều, bà áp dụng đúng những gì tôi căn dặn chứ? Bác sĩ hỏi.</p>\n<p style="text-align:justify;">\n	- Vâng, có điều các loại trái cây như: nho, táo, mận, lê, đào… thì không sao, riêng trái dừa thì mỗi lần ăn… vất vả quá.</p>\n<p style="text-align:justify;">\n	- Bác sĩ???</p>\n', '', '', 'an-ca-vo', 'nu doanh nhan, kham suc khoe, bac si', NULL, 74, '2012-03-02 06:37:09', 43, 0, NULL, NULL, 1, 3),
(45, 'chi_tai_con_ga_trong_21.jpg', 'Đi chổ khác dùm', '', '', '<p style="text-align:justify;">\n	Một chủ trang trại mua được một con gà trống cực kỳ “uy lực”. Nó không những chỉ phục vụ lũ gà mái trong trang trại, mà còn lấn sân sang địa bàn của lũ vịt trống. Nó vừa làm cho lũ vịt trống “thất nghiệp” vừa làm cho lũ vịt mái "mê mẫn" và đẻ liên tục.</p>\n', '', '', '<p style="text-align:justify;">\n	Một chủ trang trại mua được một con gà trống cực kỳ “uy lực”. Nó không những chỉ phục vụ lũ gà mái trong trang trại, mà còn lấn sân sang địa bàn của lũ vịt trống. Nó vừa làm cho lũ vịt trống “thất nghiệp” vừa làm cho lũ vịt mái "mê mẫn" và đẻ liên tục.</p>\n<p style="text-align:justify;">\n	Từ ngày mua được chú gà trống, ông chủ trang trại rất lấy làm sung sướng.</p>\n<p style="text-align:justify;">\n	Bổng một hôm, khi đi thu tiền trứng vừa về đến trang trại, ông ta nhìn thấy con gà trống nằm bất động trên bải cỏ, phía trên là một bầy quạ đang chờ ăn xác.</p>\n<p style="text-align:justify;">\n	Ông ấy rất buồn. Mặc dù khi nghĩ đến món ngọc kê tiềm thuốc bắc với tác dụng gia tăng "uy lực" đàn ông ông cũng rất thèm, nhưng trong lòng ông vẫn thấy thương tiếc con gà. Ông liền tiến đến bên xác của nó than thở lần cuối: Gà giống tội nghiệp của tao! Mầy đi rồi tao biết lấy ai để phục vụ lũ vịt mái đây?</p>\n<p style="text-align:justify;">\n	Gà trống liền hé mắt một cách bí hiểm và nói nhỏ với ông chủ: Ông làm ơn đi chỗ khác dùm cho, lũ quạ mái nó bay hết là hỏng việc của tôi đó.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'di-cho-khac-dum', 'ga trong, chu trang trai, vit trong, vit mai, qua', NULL, 96, '2012-03-03 03:57:40', 44, 0, NULL, NULL, 1, 3),
(46, 'images2.jpg', 'Khách hàng luôn luôn đúng', '', '', '<p style="text-align:justify;">\n	Thấy cô nhân viên bán hàng đang gân cổ cãi với khách hàng, ông chủ bực bội gắt:<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Thấy cô nhân viên bán hàng đang gân cổ cãi với khách hàng, ông chủ bực bội gắt:</p>\n<p style="text-align:justify;">\n	- Tôi đã bảo với cô bao nhiêu lần là khách hàng luôn luôn đúng, sao cô cứ cãi lại ông ta?</p>\n<p style="text-align:justify;">\n	- Nhưng thưa ông, ông ấy lại cứ cho là mình sai, thế mới tức chứ!</p>\n', '', '', 'khach-hang-luon-luon-dung', 'khach hang, ong chu, nhan vien', NULL, 83, '2012-03-04 01:23:37', 45, 0, NULL, NULL, 1, 3),
(47, 'art_5.gif', 'Nơi sinh tế nhị', '', '', '<p style="text-align:justify;">\n	Một anh chàng đến xin việc ở công ty nọ</p>\n<p style="text-align:justify;">\n	- Tại sao cậu không ghi đầy đủ thông tin vào mục nơi sinh? Giám đốc nhân sự hỏi.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một anh chàng đến xin việc ở công ty nọ</p>\n<p style="text-align:justify;">\n	- Tại sao cậu không ghi đầy đủ thông tin vào mục nơi sinh? Giám đốc nhân sự hỏi.</p>\n<p style="text-align:justify;">\n	- Thưa… em nghĩ…</p>\n<p style="text-align:justify;">\n	- Cậu cứ nói, việc gì phải giấu diếm? Giám đốc nhân sự nói với vẻ thông cảm.</p>\n<p style="text-align:justify;">\n	- Thưa, em nghĩ rằng chổ đó quá tế nhị ai cũng biết nên không ghi ạ. Anh ta trả lời.</p>\n<p style="text-align:justify;">\n	- ???</p>\n', '', '', 'noi-sinh-te-nhi', 'xin viec, giam doc nhan su, noi sinh', NULL, 92, '2012-03-04 01:34:57', 46, 0, NULL, NULL, 1, 3),
(48, '1318383695_doctor23.jpg', 'Bức xúc vì bị nữ y tá bắt thoát y', '', '', '<p style="text-align:justify;">\n	Một thanh niên đến bệnh viện để khắm mắt. Vừa bước vào phòng khám và chưa kịp nói gì, anh liền được cô y tá “chỉ định” cởi hết quần áo và ngồi đợi ở hành lang.</p>\n', '', '', '<p style="text-align:justify;">\n	Một thanh niên đến bệnh viện để khắm mắt. Vừa bước vào phòng khám và chưa kịp nói gì, anh liền được cô y tá “chỉ định” cởi hết quần áo và ngồi đợi ở hành lang.</p>\n<p style="text-align:justify;">\n	Mặc dù anh ta đã giải thích rõ là mình đi khám mắt, nhưng cô y tá không quan tâm và nhất quyết yêu cầu anh “thực thi mệnh lệnh”.</p>\n<p style="text-align:justify;">\n	Cuối cùng, anh ta cũng phải chấp hành theo sự chỉ dẫn của cô y tá. Ra hành lang, anh được bố trí ngồi cùng với hơn mười người khác cũng trần như nhộng.</p>\n<p style="text-align:justify;">\n	Quá bức xúc về lối làm việc tắc trách của cô y tá, anh nói với người ngồi bên cạnh: Tôi đến đây là để khám mắt, nhưng không hiểu sao họ lại bắt tôi phải cởi hết áo quần rồi ra ngồi ở đây?</p>\n<p style="text-align:justify;">\n	Nghe xong, người kia bực bội quát: Anh có vấn đề gì để bức xúc đâu? Dẫu sao anh cũng đến đây để khám bệnh. Còn tôi là giám đốc công ty vật tư y tế, tôi đến đây chỉ để tiếp thị dụng cụ y khoa mà cũng bị họ lột hết áo quần rồi tống ra đây mới tức chứ?</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'buc-xuc-vi-bi-nu-y-ta-bat-thoat-y', 'y ta, kham benh, tiep thi', NULL, 119, '2012-03-08 01:15:30', 72, 0, NULL, NULL, 1, 3),
(50, 'images6.jpg', 'Chỉ sợ mất bộ lư', '', '', '<p style="text-align:justify;">\n	Một thanh niên nghèo đến đình làng để ăn nhờ đồ cúng vào dịp lễ cầu an.</p>\n<p style="text-align:justify;">\n	Anh ta vừa bước tới cổng đình, hai bức tượng đá trước cổng đình liền đứng dậy.</p>\n', '', '', '<p style="text-align:justify;">\n	Một thanh niên nghèo đến đình làng để ăn nhờ đồ cúng vào dịp lễ cầu an.</p>\n<p style="text-align:justify;">\n	Anh ta vừa bước tới cổng đình, hai bức tượng đá trước cổng đình liền đứng dậy.</p>\n<p style="text-align:justify;">\n	Lão đại gia giàu có nhất làng thấy vậy rất ngạc nhiên và đoán mò “thằng này chắc là con trời đây”. Ông ta lân la làm quen với anh thanh niên nghèo để tìm hiểu gia cảnh.</p>\n<p style="text-align:justify;">\n	Thấy người thanh niên tuy mồ côi cha mẹ và nghèo khó nhưng có gương mặt tương đối sảng sủa, lại là “con trời” nên tay đại gia đã chủ động gã cô con gái quá lứa cho anh ta (với mong muốn trời sẽ phù hộ cho lão ta gia tăng sản nghiệp, khuếch trương thanh thế và "thanh lý" được cô con gái già "tồn kho" chứ không phải lão thương anh ta).</p>\n<p style="text-align:justify;">\n	Nhờ có sẵn tư chất thông minh, kết hợp với điều kiện kinh tế hùng mạnh của bố vợ nên chẳng bao lâu sao, anh thanh niên nghèo khó thưở trước đã trở thành doanh nhân trẻ thành đạt của làng.</p>\n<p style="text-align:justify;">\n	Đến ngày lễ cầu an, anh ta mang rất nhiều lễ vật đến đình làng để tạ ơn thần thánh.</p>\n<p style="text-align:justify;">\n	Đến cổng đình, tay đại gia trẻ nghĩ rằng thế nào hắn cũng được hai bức tượng thần giữ cổng đình đứng dậy và cúi chào kín cẫn hơn thưở trước.  </p>\n<p style="text-align:justify;">\n	Nhưng không, hai bức tượng vẫn ngồi bất động. Tay đại gia trẻ bực mình quát: “Ngày trước tôi nghèo nhưng hai vị thấy tôi liền đứng dậy chào ngay, sao ngày nay tôi đã thành triệu phú, có số má trong làng mà hai vị lại bất kính?”.</p>\n<p style="text-align:justify;">\n	Nghe vậy hai bức tượng đá ngồi tại chổ và đồng thanh quát lại: “Thằng nhải ranh kia, bọn ta chào mi khi nào? Chẳng qua ngày trước thấy mi khố rách áo ôm, bọn ta sợ mi vào đình lấy trôm bộ lư đồng nên mới đứng dậy để theo dõi. Nay mi đã giàu có rồi bọn ta không sợ mất trộm nữa nên an tâm ngồi đây. Mi chớ ngộ nhận rồi xúc phạm thần thánh mà coi chừng toi mạng”.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n<p style="text-align:justify;">\n	<br />\n	 </p>\n', '', '', 'chi-so-mat-bo-lu', 'doanh nhan tre, dinh lang, dai gia, than thanh', NULL, 112, '2012-03-12 10:07:21', 48, 0, NULL, NULL, 1, 3),
(51, '085343_trai-tim.jpg', 'Chia ly vì quà khuyến mại', '', '', '<p style="text-align:justify;">\n	Một anh chàng đang đi công tác xa nhà nhờ cửa hàng bán hoa mang tặng người yêu 19 bông hồng vào dịp sinh nhật của cô ấy, với ý nghĩa mỗi bông hoa sẽ tượng trưng cho một tuổi của người yêu.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một anh chàng đang đi công tác xa nhà nhờ cửa hàng bán hoa mang tặng người yêu 19 bông hồng vào dịp sinh nhật của cô ấy, với ý nghĩa mỗi bông hoa sẽ tượng trưng cho một tuổi của người yêu.</p>\n<p style="text-align:justify;">\n	Sau khi gọi điện cho người yêu để giải thích về ý nghĩa của món quà, anh ta liền gọi điện cho cửa hàng bán hoa để đặt 19 bông hồng đẹp nhất. Đồng thời dặn người bán hoa rằng tặng phẩm của mình phải được trao tận tay người nhận đúng giờ trong mọi điều kiện thời tiết với bất cứ giá nào.</p>\n<p style="text-align:justify;">\n	Chủ cửa hàng bán hoa rất vui và hứa thực hiện đúng theo yêu cầu của anh ta.</p>\n<p style="text-align:justify;">\n	Khi nhân viên cửa hàng bán hoa chuẩn bị đi giao hàng, nhận thấy đây là một khách hàng hào phóng và lịch thiệp, chủ cửa hàng quyết định khuyến mại thêm 10 bông hồng vào giỏ hoa.</p>\n<p style="text-align:justify;">\n	Đến tận hôm nay, khi cả hai đã “yên bề gia thất”, người thanh niên năm xưa vẫn không hiểu được tại sao khi vừa nhận hoa, cô gái đã tỏ ra tức giận và không cho anh ta cơ hội giải thích và sau đó không lâu thì cô ấy đi lấy chồng.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'chia-ly-vi-qua-khuyen-mai', 'nguoi yeu, cua hang, bong hong', NULL, 70, '2012-03-28 09:02:48', 51, 0, NULL, NULL, 1, 3),
(52, 'machinehugslarge.jpg', 'Chiêu độc của Coca-Cola: Cứ ôm là... có', '', '', '<p style="text-align:justify;">\n	Chỉ cần một cái ôm chặt thật thật nồng nàn cho chiếc máy bán hàng tự động ở Singapore, bạn sẽ nhận được một chai Coca-Cola mát lạnh mà không mất một xu nào.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	<strong>Chỉ cần một cái ôm chặt thật thật nồng nàn cho chiếc máy bán hàng tự động ở Singapore, bạn sẽ nhận được một chai Coca-Cola mát lạnh mà không mất một xu nào.</strong></p>\n<p style="text-align:justify;">\n	Chiếc máy bán hàng tự động kỳ lạ này được đặt tại ĐH Quốc gia Singapore. Forbes miêu tả, chiếc máy màu đỏ nổi bật luôn hiển thị dòng chữ “Hãy ôm tôi” to tướng ở mặt ngoài của nó. Để nhận được một chai Coca-cola từ chiếc máy, “các thượng đế” chỉ cần trả cho nó một cái ôm thật chặt, sao cho nó cảm nhận đủ sự ấm áp, nồng nàn và dễ chịu thay vì tiền.</p>\n<p style="text-align:center;">\n	<img alt="" src="/public/userfiles/images/MACHINE-HUGS-large.jpg" style="width:500px;height:365px;" /><br /><span style="font-size:11px;"><span style="color:#b22222;"><em>Chỉ cần ôm chiếc máy bán hàng tự động ở ĐH Quốc gia Singapore, bạn sẽ nhận được một chai Coca-cola mát lạnh miễn phí. Ảnh minh họa: huffingtonpost.</em></span></span><br />\n	 </p>\n<p style="text-align:justify;">\n	Đây là một chiêu tiếp thị và quảng cáo hình ảnh độc đáo mà Coca-cola - Công ty nước giải khát lớn nhất thế giới. Đồng thời, Coca-cola cũng được xếp vào danh sách là một trong những thương hiệu toàn cầu chịu khó đầu tư cho hoạt động quảng cáo, tiếp thị nhất thế giới. Đối thủ cạnh tranh lớn nhất của Coca-cola, Pepsi, cũng đang lên kế hoạch đầu tư 600 triệu USD cho các chiến dịch tiếp thị.</p>\n<p style="text-align:right;">\n	<strong>Theo: VietNamNet</strong></p>\n', '', '', 'chieu-doc-cua-coca-cola-cu-om-la-co-hang', 'coca cola, dai hoc quoc gia, singapore', NULL, 51, '2012-04-14 11:32:08', 65, 0, NULL, NULL, 1, 10),
(53, '0208.jpg', 'Nếu không gặp anh', '', '', '<p style="text-align:justify;">\n	Giám đốc một công ty dịch vụ môi trường nọ đưa vợ đi thăm nhà máy xử lý rác thải của công ty vừa khánh thành.</p>\n', '', '', '<p style="text-align:justify;">\n	<img alt="" src="/public/userfiles/images/0208.jpg" style="margin:5px;width:217px;float:left;height:150px;" />Giám đốc một công ty dịch vụ môi trường nọ đưa vợ đi thăm nhà máy xử lý rác thải của công ty vừa khánh thành.</p>\n<p style="text-align:justify;">\n	Lúc đi ngang lò đốt rác, một anh công nhân mặc đồng phục, đội nón bảo hộ lao động gọi to: Linh ơi, anh đây? Anh là Đông học chung với em hồi cấp 2 ở trường huyện đây. Lúc trước mình thường hẹn nhau ở quán chè Ba Ba đó, em nhớ không?</p>\n<p style="text-align:justify;">\n	Về đến nhà, tay giám đốc mỉa mai vợ: Em thật may mắn, nếu không chắc bây giờ em làm vợ của thằng cha công nhân đó rồi.</p>\n<p style="text-align:justify;">\n	Cô vợ phản pháo: Đúng, và anh ta còn là sếp của anh nữa chứ?</p>\n<p style="text-align:justify;">\n	Giám đốc: !!!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'neu-khong-gap-anh', 'giam doc, cong ty, duyen no', NULL, 77, '2012-04-28 08:08:25', 71, 0, NULL, NULL, 1, 3),
(54, 'sach.gif', 'Chỉ có trong sách viễn tưởng', '', '', '<p style="text-align:justify;">\n	Hiệu sách Hill là nơi lui tới của nhiều người bởi tính trung thực của đội ngũ bán hàng.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Hiệu sách Hill là nơi lui tới của nhiều người bởi tính trung thực của đội ngũ bán hàng.</p>\n<p style="text-align:justify;">\n	Một hôm, có ông khách đi vào hỏi cô bán sách:</p>\n<p style="text-align:justify;">\n	- Tôi muốn mua cuốn "Đàn ông là chúa tể của phụ nữ", ở đây có không?</p>\n<p style="text-align:justify;">\n	Rất lịch sự, cô gái đáp:</p>\n<p style="text-align:justify;">\n	- Sách viễn tưởng bán ở gian bên cạnh, thưa ông.</p>\n<p style="text-align:justify;">\n	- Thế còn cuốn "100 cách để trở thành trụ cột trong gia đình"?</p>\n<p style="text-align:justify;">\n	- Ông có thể tìm cuốn đó trong gian sách dành cho thiếu nhi.</p>\n<p style="text-align:right;">\n	PH (sưu tầm)</p>\n', '', '', 'chi-co-trong-sach-vien-tuong', 'sach thieu nh, sach khoa hoc vien tuong, cua hang sach', NULL, 39, '2012-05-02 10:59:14', 5, 0, NULL, NULL, 1, 3),
(55, 'chi_tai_con_ga_trong_27.jpg', 'Gà trống thiến', '', '', '<p style="text-align:justify;">\n	Ông chủ trại gà thả vườn nói với vợ “bà có làm gà cho tôi đãi khách nhớ bắt gà thịt thôi, cặp gà trống thiến nhớ để dành tết tới tôi biếu anh chị sui”.</p>\n', '', '', '<p style="text-align:justify;">\n	Ông chủ trại gà thả vườn nói với vợ “bà có làm gà cho tôi đãi khách nhớ bắt gà thịt thôi, cặp gà trống thiến nhớ để dành tết tới mình biếu anh chị sui”.</p>\n<p style="text-align:justify;">\n	Thằng cháu nội đang ngồi học bài nghe vậy liền hỏi:</p>\n<p style="text-align:justify;">\n	- Sao ông bà lại biếu ông bà ngoại của con gà trống thiến mà không biếu gà khác?</p>\n<p style="text-align:justify;">\n	- Gà trống thiến thì thịt nó béo hơn cháu à.</p>\n<p style="text-align:justify;">\n	- Sao nó lại béo hả ông?</p>\n<p>\n	- À, vì nó không bị lũ gà mái quấy rối nên nó béo.</p>\n<p>\n	- Quấy rối là thế nào vậy ông?</p>\n<p>\n	- Mày toàn hỏi vớ vẩn, thôi, học bài đi.</p>\n<p>\n	- Ôi, thế thì cháu biết tại sao ông và bố của cháu gầy nhom lâu nay rồi.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'ga-trong-thien', 'ga trong thien, trang trai', NULL, 57, '2012-05-19 11:08:47', 81, 0, NULL, NULL, 1, 3),
(56, 'imagescafw08cs.jpg', 'Giới thiệu người khác', '', '', '<p style="text-align:justify;">\n	Một phụ nữ đến công ty dịch vụ giới thiệu việc làm khiếu nại:</p>\n', '', '', '<p style="text-align:justify;">\n	Một phụ nữ đến công ty dịch vụ giới thiệu việc làm khiếu nại:</p>\n<p style="text-align:justify;">\n	- Các ông phải bồi thường cho tôi, cô nhân viên giúp việc mà công ty các ông giới thiệu đã bỏ trốn cùng chồng tôi từ chiều hôm qua rồi.</p>\n<p style="text-align:justify;">\n	- Ồ, nếu vậy thì không sao. Chúng tôi sẽ giới thiệu cho bà một nhân viên khác xin đẹp hơn, bảo đảm hai hôm sao chồng bà sẽ quay về ngay thôi mà.</p>\n<p style="text-align:justify;">\n	- ???</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'gioi-thieu-nguoi-khac', 'vo chong, nhan vien, cong ty', NULL, 44, '2012-05-19 11:19:02', 74, 0, NULL, NULL, 1, 3),
(57, 'imagescaj9u6oj.jpg', 'Ghét lão hàng xóm', '', '', '<p style="text-align:justify;">\n	Một nữ doanh nhân bị stress liền đến phòng khám của một bác sĩ chuyên khoa thần kinh để kiểm tra sức khỏe. Bác sĩ yêu cầu bà ta đến đứng ở cửa sổ bên hông nhà le lưỡi trong năm phút.</p>\n<p style="text-align:justify;">\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một nữ doanh nhân bị stress liền đến phòng khám của một bác sĩ chuyên khoa thần kinh để kiểm tra sức khỏe. Bác sĩ yêu cầu bà ta đến đứng ở cửa sổ bên hông nhà le lưỡi trong năm phút.</p>\n<p style="text-align:justify;">\n	Sau năm phút, nữ doanh nhân hỏi bác sĩ: Bây giờ bác sĩ có thể khám cho tôi được chứ?</p>\n<p style="text-align:justify;">\n	- Chưa đâu, thưa bà.</p>\n<p style="text-align:justify;">\n	- Nhưng tôi đã le lưỡi đủ năm phút rồi mà?</p>\n<p style="text-align:justify;">\n	- Ồ, điều đó không có ý nghĩa gì với sức khỏe của bà cả.  Chẳng qua tôi chỉ nhờ bà dọa thằng cha hàng xóm đáng ghét thôi. Bác sĩ đáp.</p>\n<p style="text-align:justify;">\n	- ???</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'ghet-lao-hang-xom', 'bac si, benh nhan, giam doc, nu doanh nhan', NULL, 57, '2012-05-19 11:33:11', 75, 0, NULL, NULL, 1, 3),
(58, 'w3jpg.jpg', 'Nguyên nhân của bệnh bò điên', '', '', '<p style="text-align:justify;">\n	Nữ phóng viên của một tờ báo nông nghiệp phỏng vấn ông giám đốc công ty chăn nuôi bò sữa: “Thưa ông, ông có thể cho biết nghi vấn của ông về bệnh bò điên?”</p>\n<p style="text-align:justify;">\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Nữ phóng viên của một tờ báo nông nghiệp phỏng vấn ông giám đốc công ty chăn nuôi bò sữa: “Thưa ông, ông có thể cho biết nghi vấn của ông về bệnh bò điên?”</p>\n<p style="text-align:justify;">\n	Vị giám đốc giải thích:</p>\n<p style="text-align:justify;">\n	- Trước tiên, cô hãy tìm hiểu quy trình nuôi bò sữa nhé. Cứ vào mỗi buổi sáng, bò cái được vắt sữa một lần và mỗi năm nó được thụ tinh một lần để sinh bò con.</p>\n<p style="text-align:justify;">\n	- Nếu vậy thì có sao đâu? Cô phóng viên hỏi.</p>\n<p style="text-align:justify;">\n	- Cô chưa hiểu thật à? Cô cứ thử tưởng tượng xem nếu người ta bóp ngực cô vào mỗi buổi sáng, nhưng một năm cô mới được thụ tinh một lần xem cô có phát điên không?</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'nguyen-nhan-cua-benh-bo-dien', 'benh bo dien, giam doc, phong vien, cong ty', NULL, 50, '2012-05-21 13:45:28', 76, 0, NULL, NULL, 1, 3),
(59, 'vietgiaitri_com688b8b78.jpg', 'Cứ yên tâm', '', '', '<p style="text-align:justify;">\n	Trong khách sạn. Một thiếu nữ xinh đẹp vừa mới thay quần áo xong thì nhân viên phục vụ phòng mở cửa đưa thức ăn vào. </p>\n', '', '', '<p style="text-align:justify;">\n	Trong khách sạn. Một thiếu nữ xinh đẹp vừa mới thay quần áo xong thì nhân viên phục vụ phòng mở cửa đưa thức ăn vào.</p>\n<p style="text-align:justify;">\n	- Anh phải gõ cửa trước mới đúng chứ! Nếu lỡ tôi còn đang thay quần áo thì sao?</p>\n<p style="text-align:justify;">\n	- Cô cứ yên tâm! Thấy cô thay xong quần áo nên tôi mới vào mà.</p>\n<p style="text-align:justify;">\n	- !!!!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'cu-yen-tam', 'thay quan ao, khach san, thieu nu', NULL, 48, '2012-05-26 08:40:07', 77, 0, NULL, NULL, 1, 3),
(60, '12.jpg', 'Dùng thử', '', '', '<p style="text-align:justify;">\n	Một anh chàng vốn kỹ tính, khi mua bất cứ hàng hóa gì cũng muốn dùng thử để biết chất lượng như thế nào rồi mới quyết định mua. Một hôm, anh ta vào quầy thuốc tây:</p>\n', '', '', '<p style="text-align:justify;">\n	Một anh chàng vốn kỹ tính, khi mua bất cứ hàng hóa gì cũng muốn dùng thử để biết chất lượng như thế nào rồi mới quyết định mua. Một hôm, anh ta vào quầy thuốc tây:</p>\n<p style="text-align:justify;">\n	- Cô ơi, bán cho tôi một hộp bao cao su. Anh ta nói với dược sĩ.</p>\n<p style="text-align:justify;">\n	Sau khi nhận hộp bao cao su từ tay cô dược sĩ, anh ta hỏi:</p>\n<p style="text-align:justify;">\n	- Chất lượng nó thế nào vậy cô?</p>\n<p style="text-align:justify;">\n	- Rất tốt, đây là hàng chất lượng cao mà. Nữ dược sĩ trả lời.</p>\n<p style="text-align:justify;">\n	- Cô có thể cho tôi dùng thử một cái tại đây được không?</p>\n<p style="text-align:justify;">\n	- !!!!!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'dung-thu', 'dung thu, bao cao su, duoc si', NULL, 54, '2012-05-26 08:42:08', 78, 0, NULL, NULL, 1, 3),
(61, 'danhgiay612012_cc3ac.jpg', 'An ủi nhầm đối tượng', '', '', '<p style="text-align:justify;">\n	Một doanh nhân ăn mặc sang trọng ngồi trong nhà hàng, đưa chân cho ông già đánh giày.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một doanh nhân ăn mặc sang trọng ngồi trong nhà hàng, đưa chân cho ông già đánh giày. Tỏ vẻ thương cảm, anh ta an ủi:</p>\n<p style="text-align:justify;">\n	- Trước đây tôi cũng có lúc nghèo khổ, túng thiếu phải đi đánh giày đấy. Bây giờ tôi đổi đời rồi và đang là nhà đầu tư chứng khoán.</p>\n<p style="text-align:justify;">\n	- Còn tôi, cách đây mấy năm tôi cũng mua bán chứng khoán.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'an-ui-nham-doi-tuong', 'doanh nhan, ngheo kho, chung khoan', NULL, 48, '2012-05-29 07:38:00', 79, 0, NULL, NULL, 1, 3),
(62, 'manggi.jpg', 'Em mạng gì?', '', '', '<p>\n	Một giám đốc doanh nghiệp vốn mê tính hỏi hỏi một nữ ứng viên trong khi phỏng vấn tuyển dụng:</p>\n', '', '', '<p>\n	Một giám đốc doanh nghiệp vốn mê tính hỏi một nữ ứng viên trong khi phỏng vấn tuyển dụng:</p>\n<p>\n	- Em mạng gì?</p>\n<p>\n	- Dạ, trước đây em dùng mạng Vina, bây giờ em chuyển sang dùng Mobi rồi ạ. Cô gải thong thả trả lời.</p>\n<p>\n	- !!!</p>\n', '', '', 'em-mang-gi', 'giam doc, ung vien, vina mobi', NULL, 44, '2012-06-07 11:42:46', 80, 0, NULL, NULL, 1, 3),
(63, 'nhau.jpg', 'Đối nhân xử thế', '', '', '<p style="text-align:justify;">\n	Hai ông chủ doanh nghiệp chia xẻ với nhau trong lúc trà dư tửu hậu:</p>\n', '', '', '<p style="text-align:justify;">\n	Hai ông chủ doanh nghiệp chia xẻ với nhau trong lúc trà dư tửu hậu:</p>\n<p style="text-align:justify;">\n	Thứ nhất là: Phải biết điềm tĩnh trước gái xinh và không giật mình trước gái xấu.</p>\n<p>\n	<span style="text-align:justify;">Thứ hai là: Không được đầu gấu với gái ngoan và không cần nhẹ nhàng với gái dữ.</span></p>\n<p style="text-align:justify;">\n	Thứ ba là: Không được tự tử nếu mất gái ngon và không ngậm bồ hòn ôm gái nát.</p>\n<p>\n	<span style="text-align:justify;">Thứ tư là: Không được bộc phát thích gái teen và không được ném mình vào gái ế.</span></p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong><br />\n	 </p>\n', '', '', 'doi-nhan-xu-the', 'anh em, trai gai, an nhau, chu doanh nghiep', NULL, 21, '2012-06-22 12:00:40', 82, 0, NULL, NULL, 1, 3);
INSERT INTO `dos_module_entertainment` (`record_id`, `pic_thumb`, `title`, `titleen`, `titlefr`, `preview`, `previewen`, `previewfr`, `content`, `contenten`, `contentfr`, `tag`, `description`, `author`, `hits`, `postdate`, `record_order`, `record_type`, `extra_field1`, `extra_field2`, `enable`, `dos_module_item_cat_cat_id`) VALUES
(64, 'imagescaa95n3n.jpg', 'Truyện cười ngắn', '', '', '<p style="text-align:justify;">\n	Hai nữ doanh nhân độc thân nói chuyện với nhau</p>\n<p style="text-align:justify;">\n	- Bây giờ trai đẹp rất hiếm</p>\n<p style="text-align:justify;">\n	- Đã vậy mà chúng nó còn yêu nhau nữa, có chết bọn mình không chứ?</p>\n', '', '', '<p style="text-align:justify;">\n	Hai nữ doanh nhân độc thân nói chuyện với nhau</p>\n<p style="text-align:justify;">\n	- Bây giờ trai đẹp rất hiếm</p>\n<p style="text-align:justify;">\n	- Đã vậy mà chúng nó còn yêu nhau nữa, có chết bọn mình không chứ?</p>\n<p style="text-align:justify;">\n	***</p>\n<p style="text-align:justify;">\n	<strong>Doanh nhân và chân dài - Phần 1</strong></p>\n<p style="text-align:justify;">\n	Doanh nhân và chân dài tâm sự:</p>\n<p style="text-align:justify;">\n	- Ưu điểm của anh là gì? Chân dài hỏi.</p>\n<p style="text-align:justify;">\n	- Rất nhiều tiền.</p>\n<p style="text-align:justify;">\n	- Còn khuyết điểm?</p>\n<p style="text-align:justify;">\n	- À, thì có rất nhiều ưu điểm. Doanh nhân đắc chí trả lời.</p>\n<p style="text-align:justify;">\n	- !!!</p>\n<p style="text-align:justify;">\n	***</p>\n<p style="text-align:justify;">\n	<strong>Doanh nhân và chân dài - Phần 2</strong></p>\n<p style="text-align:justify;">\n	- Chân dài: Trẻ em thật dễ thương</p>\n<p style="text-align:justify;">\n	- Doanh nhân: Nhưng quá trình tạo ra chúng mới đáng để nhớ.</p>\n<p style="text-align:justify;">\n	***</p>\n<p style="text-align:justify;">\n	<strong>Ý tưởng kinh doanh độc đáo</strong></p>\n<p style="text-align:justify;">\n	Tại phòng đăng ký kinh doanh ở tỉnh nọ:</p>\n<p style="text-align:justify;">\n	- Anh kinh doanh ngành nghề gì? Chị công chức hỏi khách đến làm thủ tục đăng ký kinh doanh.</p>\n<p style="text-align:justify;">\n	- Nhận trông các bé gái tuổi từ 16 đến 22, thời gian từ 18h tối đến 6h sáng hôm sau, cả thứ 7 và chủ nhật. Giá cả thoả thuận.</p>\n<p style="text-align:justify;">\n	- ???</p>\n<p style="text-align:justify;">\n	***</p>\n<p style="text-align:justify;">\n	<strong>Thông báo tuyển dụng</strong></p>\n<p style="text-align:justify;">\n	Một thông báo tuyển dụng ghi:</p>\n<p style="text-align:justify;">\n	Cần tuyển 20 công nhân nam, quyền lợi gồm:</p>\n<p style="text-align:justify;">\n	+ Lương thỏa thuận, có BHXH</p>\n<p style="text-align:justify;">\n	+ Cơm trưa miễn phí</p>\n<p style="text-align:justify;">\n	+ Nhà trọ miễn phí</p>\n<p style="text-align:justify;">\n	+ Bóp vú hàng ngày</p>\n<p style="text-align:justify;">\n	Liên hệ: Nông trường bò sữa X</p>\n<p style="text-align:justify;">\n	***</p>\n<p style="text-align:justify;">\n	<strong>Anh ơi em có bầu</strong></p>\n<p style="text-align:justify;">\n	Đây là lần đầu tiên trong đời mình gặp một người con gái đứng trước mặt và nói:</p>\n<p style="text-align:justify;">\n	"Anh ơi, Em có bầu..."</p>\n<p style="text-align:justify;">\n	Thực sự mình cực kỳ hoảng sợ, lo lắng và mình không ngờ nó lại đến nhanh như vậy, quá bàng hoàng, đến mức không tin được, không biết sau đấy mình sẽ ra sao nữa...</p>\n<p style="text-align:justify;">\n	.. Thôi đành ...</p>\n<p style="text-align:justify;">\n	... đứng dậy nhường ghế cho em ấy và...</p>\n<p style="text-align:justify;">\n	... đứng cả tiếng đồng hồ trên xe bus ...</p>\n<p style="text-align:justify;">\n	Hôm qua cậu kế toán trưởng hỏi mượn xe con của mình để đi hỏi vợ cho thằng em. Là sếp mà, mình phải giúp bọn nó chứ sao? Đó là lý do mình đi xe bus. </p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'truyen-cuoi-ngan', 'doanh nhan, chan dai, sex, tinh duc', NULL, 39, '2012-06-23 03:11:18', 85, 0, NULL, NULL, 1, 3),
(65, 'loaloa.jpg', 'Ai phát minh ra ĐTDĐ đầu tiên?', '', '', '<p style="text-align:justify;">\n	Tại một cuộc hội thảo quốc tế về công nghệ điện tử, viễn thông được tổ chức tại Phần Lan, quê hương của hãng điện thoại di động Nokia lừng danh thế giới.<br />\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Tại một cuộc hội thảo quốc tế về công nghệ điện tử, viễn thông được tổ chức tại Phần Lan, quê hương của hãng điện thoại di động Nokia lừng danh thế giới.</p>\n<p style="text-align:justify;">\n	- Gần đây, các nhà khoa học đã phát hiện một sợi cáp đồng 500 năm tuổi, được chôn sâu 200 mét dưới lòng đất ngay tại Washington DC. Điều đó chứng tỏ rằng người Mỹ đã có kinh nghiệm sử dụng điện thoại bàn từ rất lâu. Đại diện một hãng sản xuất thiết bị viễn thông Mỹ phát biểu.</p>\n<p style="text-align:justify;">\n	- Khi khai quật một thành cổ 2.000 năm tuổi ở Trung Quốc, các chuyên gia đã tìm thấy một sợi cáp quang được sản xuất cùng niên đại tại đây. Điều đó chứng minh rằng ngay từ rất sớm, người Trung Hoa chúng tôi đã sử dụng truyền hình cáp. Đại diện một công ty điện tử của Trung Quốc phát biểu.</p>\n<p style="text-align:justify;">\n	- Các nhà khoa học quốc tế (có cả người Mỹ và người Trung Quốc tham gia) đã xác nhận rằng, khi khai quật một đô thị cổ 4.000 năm tuổi ở Việt Nam, ngoài những chủng loại hiện vật quen thuộc ra, họ chẳng tìm thấy gì khác. Điều đó nói lên rằng, tổ tiên của người Việt mới chính là chủ nhân của những chiếc điện thoại di động đầu tiên trên thế giới. Đại diện một hãng điện thoại di động của Việt Nam phát biểu.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'ai-phat-minh-ra-dtdd-dau-tien', 'dien tu, vien thong, dien thoai di dong, khao co, khai quat', NULL, 17, '2012-06-23 03:36:38', 88, 0, NULL, NULL, 1, 3),
(66, 'chacon.jpg', 'Tiền ở đâu ra?', '', '', '<p style="text-align:justify;">\n	Một giám đốc vừa về đến nhà sau một tuần đi công tác đã rất đổi ngạc nhiên khi thấy cậu con trai của mình có chiếc điện thoại mới.</p>\n', '', '', '<p style="text-align:justify;">\n	Một giám đốc vừa về đến nhà sau một tuần đi công tác đã rất đổi ngạc nhiên vì thấy cậu con trai của mình có chiếc điện thoại mới.</p>\n<p style="text-align:justify;">\n	- Ở đâu mà con có điện thoại mới vậy, nó tới tới mấy triệu đồng một cái mà? Ông ta hỏi.</p>\n<p style="text-align:justify;">\n	- Con mua từ tiền con có được nhờ đi chơi đó bố à. Cậu bé trả lời.</p>\n<p style="text-align:justify;">\n	- Con trai à, làm gì có ai đi chơi mà có tiền nào, con hãy nói cho bố biết sự thật đi, ở đâu mà con có tiền? </p>\n<p style="text-align:justify;">\n	- Con nói thật mà bố chẳng chịu tin gì cả. Từ khi bố vắng nhà, cứ mỗi buổi tối, ông Bảy chủ tiệm vàng đều tới nhà gặp mẹ. Ông ấy bảo con đi chơi đi và cho con 500 ngàn.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n<p>\n	 </p>\n', '', '', 'tien-o-dau-ra', 'dao choi, thu nhap, kiem tien', NULL, 84, '2012-06-24 12:46:45', 87, 0, NULL, NULL, 1, 3),
(67, 'nguoiduatintien.jpg', 'Được giữ lại tiền thừa', '', '', '<p style="text-align:justify;">\n	Một khách hàng tới quầy tính tiền của siêu thị nói:</p>\n<p>\n	 </p>\n<p>\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một khách hàng tới quầy tính tiền của siêu thị nói:</p>\n<p>\n	 </p>\n<p style="text-align:justify;">\n	- Hôm qua tôi mua hàng ở đây và hóa đơn bị tính nhầm tới 100 USD.</p>\n<p style="text-align:justify;">\n	- Quá muộn thưa ông - người quản lý siêu thị nhún vai. Đáng lẽ ông phải nói ngay từ hôm qua vì bây giờ chúng tôi đã khóa sổ rồi.</p>\n<p>\n	 </p>\n<p style="text-align:justify;">\n	- Có nghĩa là tôi được giữ lại 100 USD tiền thừa này? ông khách mừng rỡ.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'duoc-giu-lai-tien-thua', 'sieu thi, thoi tien, gian lan', NULL, 74, '2012-06-24 12:53:13', 86, 0, NULL, NULL, 1, 3),
(68, 'thu_ky_iso.jpg', 'Sếp ở trong toilet', '', '', '<p style="text-align:justify;">\n	Cô thư ký mới vào làm việc ngày đầu tiên. Trưởng phòng tổ chức đang hướng dẫn công việc cho cô thì sếp ra khỏi phòng. Ngay lúc đó, chuông điện thoại reo.</p>\n<p>\n	 </p>\n<p style="text-align:justify;">\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Cô thư ký mới vào làm việc ngày đầu tiên. Trưởng phòng tổ chức đang hướng dẫn công việc cho cô thì sếp ra khỏi phòng. Ngay lúc đó, chuông điện thoại reo.</p>\n<p>\n	 </p>\n<p style="text-align:justify;">\n	Cô thư ký cầm ống nghe và trả lời trông rất chuyên nghiệp, nhưng lại bật ra câu:</p>\n<p style="text-align:justify;">\n	- Dạ, hiện giờ sếp tôi đang ở trong toilet ạ!</p>\n<p>\n	 </p>\n<p style="text-align:justify;">\n	- Ồ không - Trưởng phòng nhắc - Nói là sếp đang ngồi với khách.</p>\n<p style="text-align:justify;">\n	- Dạ, hiện giờ sếp tôi đang ở trong toilet với khách ạ! - Cô thư ký nhanh nhảu sửa lời liền với người nghe!</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'sep-o-trong-toilet', 'sep, thu ky van phong, nhan vien, toilet', NULL, 9, '2012-06-26 11:18:41', 84, 0, NULL, NULL, 1, 3),
(69, 'larger_ofy1336108119.jpg', 'Thợ sửa khóa trong nhà', '', '', '<p style="text-align:justify;">\n	Một ông giám đốc doanh nghiệp đi công tác về, gọi cửa mãi không thấy ai ra mở cửa nên ông ta bực bội phá cửa vào nhà. Vợ ông ta thấy vậy hớt hải từ phòng ngủ chạy ra và la lớn.</p>\n<p style="text-align:justify;">\n	 </p>\n', '', '', '<p style="text-align:justify;">\n	Một ông giám đốc doanh nghiệp đi công tác về, gọi cửa mãi không thấy ai ra mở cửa nên ông ta bực bội phá cửa vào nhà. Vợ ông ta thấy vậy hớt hải từ phòng ngủ chạy ra và la lớn.<br /><br />\n	- Tôi chờ ông mấy ngày thì không sao, ông mới chờ có mấy phút mà đã phá cửa! May mà đang có ông thợ sửa khóa trong nhà, để tôi nói anh ta ra sửa lại.</p>\n<p style="text-align:right;">\n	<strong>PH (sưu tầm)</strong></p>\n', '', '', 'tho-sua-khoa-trong-nha', 'chu doanh nghiep, tho dien, ngoai tinh, sex, tinh duc', NULL, 20, '2012-06-28 14:33:59', 89, 0, NULL, NULL, 1, 3),
(70, 'thumbnailhandler.jpg', 'Henry Ford và Thượng Đế', '', '', '<p style="text-align:justify;">\n	Ông Henry Ford là cha đẻ của nghành công nghiệp xe hơi thế giới khi từ trần được lên thiên đàng. </p>\n', '', '', '<p style="text-align:justify;">\n	Ông Henry Ford là cha đẻ của nghành công nghiệp xe hơi thế giới khi từ trần được lên thiên đàng. Tại cửa thiên đàng Thánh Phê-Rô (St. Peter) chào mừng Henry Ford và nói: “ Tốt, con là người tốt và sự phát minh ra dây chuyền lắp ráp trong kỹ nghệ xe hơi của con đã làm thay đổi bộ mặt công nghệ xe hơi thế giới. Để trọng thưởng con, con có thể được quyền giao tiếp với bất cứ ai trên Thiên Đàng mà con thích”. Ông Ford suy nghĩ rồi đáp lại, “ Con xin được giao tiếp với Thượng Đế”.</p>\n<p>\n	<span style="text-align:justify;">Thánh Phê-Rô đưa ông Ford đến cung điện của Thượng đế và giới thiệu Ford với Thượng Đế. Ông Ford liền hỏi Thượng Đế:</span></p>\n<p style="text-align:justify;">\n	“Ngài đã nghĩ  như thè nào khi Ngài sáng tác ra người phụ nữ?”</p>\n<p>\n	<span style="text-align:justify;">Thượng Đế hỏi lại: “ Ý của con là thế nào”</span></p>\n<p style="text-align:justify;">\n	Ông Ford trả lời: “Người phụ nử do Thượng Đế sáng tác có quá nhiều lỗi trầm trọng, điểm qua sơ sơ vài cái nhé:</p>\n<p>\n	<span style="text-align:justify;">1.Phần trước và sau bị nhô ra quá nhiều (tức là ngực tấn công, mông phòng thủ :-) )</span></p>\n<p style="text-align:justify;">\n	2.Ở tốc độ nhanh quá nhiều tiếng ồn lạch cạch.</p>\n<p style="text-align:justify;">\n	3.Tiền bảo trì quá tốn kém.</p>\n<p style="text-align:justify;">\n	4.Phải được thường xuyên sơn phết và tái tu sửa.</p>\n<p style="text-align:justify;">\n	5.Mỗi 28 ngày là có khoảng 5-6 ngày không xử dụng được.</p>\n<p style="text-align:justify;">\n	6.Phần sau thì bị lắc qua lắc lại quá nhiều.</p>\n<p style="text-align:justify;">\n	7.Chổ dẫn vào quá gần với chổ thải ra.</p>\n<p style="text-align:justify;">\n	8.Đèn pha thì thường là quá nhỏ.</p>\n<p style="text-align:justify;">\n	9.Sức tiêu thụ xăng dầu thì quá mãnh liệt.</p>\n<p style="text-align:justify;">\n	Nghe đến đây Thượng đế trả lời: “ Hmmm, chờ ta 1 phút”.</p>\n<p style="text-align:justify;">\n	Thượng đế đến cái máy siêu điện toán Celestial, gõ vài phím và chờ đợi kết quả. Liền sau đó máy điện toán in ra kết quả, và Thượng đế đọc báo cáo rồi bảo ông Ford:</p>\n<p>\n	<span style="text-align:justify;">“Rất có thể tác phẩm sáng tạo của ta bị nhiều sai lỗi, nhưng theo báo cáo thống kê này thì nhiều đàn ông thích lái sản phẩm của ta hơn của con”.</span></p>\n<p style="text-align:right;">\n	<strong><span style="text-align:justify;">PH (sưu tầm)</span></strong></p>\n', '', '', 'henry-ford-va-thuong-de', 'Henry Ford, thuong de, xe hoi, phu nu\n', NULL, 20, '2012-06-28 15:02:42', 90, 0, NULL, NULL, 1, 3);

