<?php if ($item): ?>    <?php $this->breadcrumbs = array($this->lang[$this->module->id] => array($this->setUrlModule()), $item['title']); ?>    <?php $this->pageTitle = $item['title'] . ' - ' . $this->lang[$this->module->id];    $this->setDescription($item['description']) ?>    <div>        <a href="<?php echo $item['website']?>">            <img src="<?php echo Yii::app()->baseUrl . Yii::getPathOfAlias('filePathUpload') . '/image/' . $this->module->id . '/' . $item->pic_full ?>" alt="<?php echo $item['title'] ?>" />         </a>        <p><?php echo $item['title'] ?></p>        <p>Địa chỉ: <?php echo $item['address']; ?></p>        <p>Website: <a href="<?php echo $item['website']; ?>"><?php echo $item['website']; ?></a></p>        <p><?php echo $item['description']; ?></p>    </div><?php else: ?>    No record<?php endif; ?>