<?php $this->breadcrumbs = array($this->lang[$this->module->id]); ?><?php $this->pageTitle = $this->lang[$this->module->id];$this->setDescription()?><ul class="customers">    <?php if ($item): ?>    <?php foreach ($item['models'] as $value): ?>            <li>				<div class="customers-img">					<?php echo CHtml::link('<img src="<?php echo Yii::app()->baseUrl . Yii::getPathOfAlias('filePathUpload') . '/image/' . $this->module->id . '/' . $value->pic_thumb ?>" alt="<?php echo $value['title'] ?>" />', array($this->setUrlModule('customer') .'/'. $value['tag'] . '.html'), array('title' => $value['title']))?>				</div>                <h3 class="title-tomers">					<?php echo CHtml::link($value['title'], array($this->setUrlModule('customer') .'/'. $value['tag'] . '.html'), array('title' => $value['title']))?>				</h3>            </li>        <?php endforeach; ?>    <?php else: ?>        No record<?php endif; ?></ul>