<?php if ($item): ?>    <?php $this->breadcrumbs = array($this->lang[$this->module->id] => array($this->setUrlModule()), $item['title']); ?>    <?php $this->pageTitle = $item['title'] . ' - ' . $this->lang[$this->module->id];    $this->setDescription($item['description']) ?>    <div class="customer-img-info">        <a href="<?php echo $item['website']?>">            <img src="<?php echo Yii::app()->baseUrl . Yii::getPathOfAlias('filePathUpload') . '/image/' . $this->module->id . '/' . $item->pic_full ?>" alt="<?php echo $item['title'] ?>" />         </a>        <h2 class=""><strong><?php echo $item['title'] ?></strong></h2>        <p>Địa chỉ:<?php echo $item['address']; ?></p>        <p class="link-customer">Website: <a href="<?php echo $item['website']; ?>" target="_blank"><?php echo $item['website']; ?></a></p>        <p><?php echo $item['description']; ?></p>    </div><?php else: ?>    No record<?php endif; ?>