<?php $num = count($services_list_home); $i=1; foreach($services_list_home as $value):?><?php $last = ($i==$num) ? 'panel-right' : 'panel' ?><div class="<?php echo $last?>">	<?php if($value['pic_thumb']):?>		<div class="img-panel"><?php echo CHtml::link(CHtml::image(Yii::app()->baseUrl.Yii::getPathOfAlias('filePathUpload').'/image/services/'.$value['pic_thumb'], $value->ServicesLanguage[Yii::app()->language]['title']) , array($this->setUrlModule('services').'/'.$value->ServicesLanguage[Yii::app()->language]['tag'].'.html'), array('title'=>$value->ServicesLanguage[Yii::app()->language]['title'])); ?></div>	<?php endif?>	<h3 class="title-panel"><?php echo $value->ServicesLanguage[Yii::app()->language]['title']?></h3>	<p><?php echo $value->ServicesLanguage[Yii::app()->language]['description']?></p>    <?php echo CHtml::link('Read more' , array($this->setUrlModule('services').'/'.$value->ServicesLanguage[Yii::app()->language]['tag'].'.html'), array('title'=>$value->ServicesLanguage[Yii::app()->language]['title'])); ?></div><?php $i++; endforeach?><div class="clear"></div>