<?php $this->breadcrumbs = array($this->lang[$this->module->id]); ?><?php $this->pageTitle = $this->lang[$this->module->id]; $this->setDescription()?><?php if ($items): ?>    <?php foreach($items as $value):?>        <?php $type = ($value['record_type'] == 'da-giao-dich') ? 'class="da-giao-dich' : (($value['record_type'] == 'da-dat-cho') ? 'class="da-dat-cho' : 'class="chua-giao-dich"')?>        <p><?php echo $value['title']?> - <?php echo $type?></p>    <?php endforeach?><?php else: ?>    <?php echo $this->lang['no_record']?><?php endif;?>