--
-- Table structure for table `dos_module_knows_cat`
--

CREATE TABLE IF NOT EXISTS `dos_module_knows_cat` (
  `cat_id` int(11) NOT NULL AUTO_INCREMENT,
  `cat_parent_id` int(11) NOT NULL DEFAULT '0',
