<?php if ($item): ?>    <?php $this->breadcrumbs = array($this->lang[$this->module->id] => array($this->setUrlModule()), $item['title']); ?>	<?php $this->pageTitle = $item['title'] . ' - ' . $this->lang[$this->module->id]; $this->setDescription($item['description']) ?>    <div class="frame-panel">        <h2 class="title-right"><?php echo $item['title']?></h2>        <div class="frame-tent-right">            <?php echo $item['content']?>        </div>    </div>		<?php if($otherItems):?>		<h4 class="title-item-other"><?php echo $this->lang['other_post']?></h4>		<ul class="panel-items-other">			<?php foreach($otherItems as $value):?>				<li>					<?php echo CHtml::link($value->ItemLanguage[Yii::app()->language]['title'], array($this->setUrlModule('recruitment').'/'.$value->ItemLanguage[Yii::app()->language]['tag'].'.html'), array('title' =>$value->ItemLanguage[Yii::app()->language]['title']))?>				</li>			<?php endforeach?>		</ul>	<?php endif?><?php else: ?>    No record<?php endif; ?>